import React from "react";
import { Button } from "@/components/ui/button";
import Image from "@/components/Image";
import Link from "@/components/Link";

// Local types reflecting app-level Image and Link contracts
export interface AppImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface AppLinkProps {
  href: string;
  target?: "_blank" | "_self";
  className?: string;
  rel?: string;
}

export interface Feature1Props {
  title: string;
  description?: string;
  image: AppImageProps;
  buttonPrimary: { label: string } & AppLinkProps;
  buttonSecondary: { label: string } & AppLinkProps;
  backgroundColor?: string;
  backgroundImage?: AppImageProps;
}

export const Feature1: React.FC<Feature1Props> = ({
  title,
  description,
  image,
  buttonPrimary,
  buttonSecondary,
  backgroundColor,
  backgroundImage,
}) => {
  const primaryRel = buttonPrimary.target === "_blank" && !buttonPrimary.rel ? "noopener noreferrer" : buttonPrimary.rel;
  const secondaryRel = buttonSecondary.target === "_blank" && !buttonSecondary.rel ? "noopener noreferrer" : buttonSecondary.rel;

  return (
    <section className="relative py-32" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage && (
        <Image
          {...backgroundImage}
          fill
          className={`absolute inset-0 -z-10 object-cover ${backgroundImage.className ?? ""}`}
        />
      )}
      <div className="container">
        <div className="grid items-center gap-8 lg:grid-cols-2">
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">{title}</h1>
            {description && (
              <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">{description}</p>
            )}
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link href={buttonPrimary.href} target={buttonPrimary.target} rel={primaryRel} className={buttonPrimary.className}>
                  {buttonPrimary.label}
                </Link>
              </Button>
              <Button variant="outline" asChild>
                <Link href={buttonSecondary.href} target={buttonSecondary.target} rel={secondaryRel} className={buttonSecondary.className}>
                  {buttonSecondary.label}
                </Link>
              </Button>
            </div>
          </div>
          <Image
            {...image}
            className={`max-h-96 w-full rounded-md object-cover ${image.className ?? ""}`}
          />
        </div>
      </div>
    </section>
  );
};

export default Feature1;
