import React from "react";
import { Button } from "@/components/ui/button";
import Image from "@/components/Image";
import Link from "@/components/Link";

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface FeatureButton {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Feature2Props {
  title: string;
  description?: string;
  image: ImageProps;
  buttonPrimary: FeatureButton;
  buttonSecondary: FeatureButton;
  backgroundImage?: ImageProps;
  backgroundColor?: string;
}

export const Feature2: React.FC<Feature2Props> = ({
  title,
  description,
  image,
  buttonPrimary,
  buttonSecondary,
  backgroundImage,
  backgroundColor,
}) => {
  return (
    <section className="relative py-32" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage && (
        <div className="absolute inset-0 -z-10 overflow-hidden">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={`${backgroundImage.className ?? ""} h-full w-full object-cover`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      )}
      <div className="container">
        <div className="grid items-center gap-8 md:gap-16 lg:grid-cols-2">
          <Image
            src={image.src}
            alt={image.alt}
            width={image.width}
            height={image.height}
            priority={image.priority}
            quality={image.quality}
            sizes={image.sizes}
            fill={image.fill}
            className={`${image.className ?? ""} max-h-96 w-full rounded-md object-cover`}
            placeholder={image.placeholder}
          />
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">{title}</h1>
            {description && (
              <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">{description}</p>
            )}
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link href={buttonPrimary.href} target={buttonPrimary.target} rel={buttonPrimary.rel}>
                  {buttonPrimary.label}
                </Link>
              </Button>
              <Button variant="outline" asChild>
                <Link href={buttonSecondary.href} target={buttonSecondary.target} rel={buttonSecondary.rel}>
                  {buttonSecondary.label}
                </Link>
              </Button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
