import React from "react";
import { cn } from "@/lib/utils";
import Image from "@/components/Image";

// ImageProps structure per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface MarqueeLogo {
  image: ImageProps;
  gradient: {
    from: string;
    via: string;
    to: string;
  };
}

export interface MarqueeLogoScrollerProps extends React.HTMLAttributes<HTMLDivElement> {
  title: string;
  description: string;
  logos: MarqueeLogo[];
  speed?: "normal" | "slow" | "fast";
  backgroundImage?: ImageProps; // optional background image
  backgroundColor?: string; // optional background color
}

const MarqueeLogoScroller = React.forwardRef<HTMLDivElement, MarqueeLogoScrollerProps>(
  (
    {
      title,
      description,
      logos,
      speed,
      backgroundImage,
      backgroundColor,
      className,
      style,
      ...rest
    },
    ref
  ) => {
    const durationMap: Record<NonNullable<MarqueeLogoScrollerProps["speed"]>, string> = {
      normal: "40s",
      slow: "80s",
      fast: "5s",
    };

    const animationDuration = speed ? durationMap[speed] : undefined;

    const sectionStyle: React.CSSProperties = {
      ...style,
      ...(backgroundColor ? { backgroundColor } : {}),
    };

    return (
      <>
        <style>{`
          @keyframes marquee {
            from { transform: translateX(0); }
            to { transform: translateX(-50%); }
          }
        `}</style>

        <section
          ref={ref}
          aria-label={title}
          className={cn(
            "relative w-full bg-background text-foreground rounded-lg border overflow-hidden",
            className
          )}
          style={sectionStyle}
          {...rest}
        >
          {backgroundImage ? (
            <Image
              src={backgroundImage.src}
              alt={backgroundImage.alt}
              width={backgroundImage.width}
              height={backgroundImage.height}
              priority={backgroundImage.priority}
              quality={backgroundImage.quality}
              sizes={backgroundImage.sizes}
              fill={backgroundImage.fill}
              className={cn("absolute inset-0 object-cover", backgroundImage.className)}
              placeholder={backgroundImage.placeholder}
            />
          ) : null}

          <div className="p-6 md:p-8 lg:p-10 relative">
            <div className="grid grid-cols-1 lg:grid-cols-[3fr_2fr] gap-6 lg:gap-8 pb-6 md:pb-8 border-b">
              <h2 className="text-3xl md:text-4xl font-semibold tracking-tighter text-balance">{title}</h2>
              <p className="text-muted-foreground self-start lg:justify-self-end text-balance">{description}</p>
            </div>
          </div>

          <div
            className="w-full overflow-hidden"
            style={{
              maskImage: "linear-gradient(to right, transparent, black 10%, black 90%, transparent)",
            }}
          >
            <div
              className="flex w-max items-center gap-4 py-4 pr-4 hover:[animation-play-state:paused] transition-all duration-300 ease-in-out"
              style={animationDuration ? { animation: `marquee ${animationDuration} linear infinite` } : undefined}
            >
              {[...logos, ...logos].map((logo, index) => (
                <div
                  key={index}
                  className="group relative h-24 w-40 shrink-0 flex items-center justify-center rounded-lg bg-secondary/70 overflow-hidden"
                >
                  <div
                    style={
                      {
                        "--from": logo.gradient.from,
                        "--via": logo.gradient.via,
                        "--to": logo.gradient.to,
                      } as React.CSSProperties
                    }
                    className="absolute inset-0 scale-150 opacity-0 transition-all duration-700 ease-out group-hover:opacity-100 group-hover:scale-100 bg-gradient-to-br from-[var(--from)] via-[var(--via)] to-[var(--to)]"
                  />
                  <Image
                    src={logo.image.src}
                    alt={logo.image.alt}
                    width={logo.image.width}
                    height={logo.image.height}
                    priority={logo.image.priority}
                    quality={logo.image.quality}
                    sizes={logo.image.sizes}
                    fill={logo.image.fill}
                    className={cn("relative h-3/4 w-auto object-contain", logo.image.className)}
                    placeholder={logo.image.placeholder}
                  />
                </div>
              ))}
            </div>
          </div>
        </section>
      </>
    );
  }
);

MarqueeLogoScroller.displayName = "MarqueeLogoScroller";

export { MarqueeLogoScroller };
