import React from "react";
import Image from "@/components/Image";

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface StatItem {
  value: string;
  label: string;
}

export interface StatsSectionProps {
  title: string;
  description?: string;
  stats: StatItem[];
  backgroundImage?: ImageProps;
  backgroundColor?: string;
}

export default function StatsSection({ title, description, stats, backgroundImage, backgroundColor }: StatsSectionProps) {
  return (
    <section className="relative py-12 md:py-20" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage && (
        <Image
          {...backgroundImage}
          className={`absolute inset-0 -z-10 h-full w-full object-cover ${backgroundImage.className ?? ""}`}
        />
      )}
      <div className="mx-auto max-w-5xl space-y-8 px-6 md:space-y-16">
        <div className="relative z-10 mx-auto max-w-xl space-y-6 text-center">
          <h2 className="text-4xl font-medium lg:text-5xl">{title}</h2>
          {description && <p>{description}</p>}
        </div>

        <div className="grid gap-12 divide-y md:grid-cols-3 md:gap-2 md:divide-x md:divide-y-0">
          {stats?.map((item, idx) => (
            <div key={idx} className="space-y-4 text-center">
              <div className="text-5xl font-bold">{item.value}</div>
              <p>{item.label}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
