import React from "react";
import Image from "@/components/Image";

// Per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface TeamMember {
  name: string;
  role: string;
  avatar: ImageProps;
}

interface TeamGroup {
  heading: string;
  members: TeamMember[];
}

export interface TeamSectionProps {
  title: string; // e.g., "Our team"
  groups: TeamGroup[]; // e.g., Leadership, Engineering, Marketing
  backgroundImage?: ImageProps; // optional background image
  backgroundColor?: string; // optional background color (e.g., hex, rgb, tailwind via class not here)
  sectionClassName?: string;
  containerClassName?: string;
}

const TeamSection: React.FC<TeamSectionProps> = ({
  title,
  groups,
  backgroundImage,
  backgroundColor,
  sectionClassName,
  containerClassName,
}) => {
  return (
    <section
      className={`relative py-12 md:py-32${sectionClassName ? ` ${sectionClassName}` : ""}`}
      style={{ backgroundColor }}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={`${backgroundImage.className ? backgroundImage.className + " " : ""}h-full w-full object-cover`}
          />
        </div>
      ) : null}

      <div className={`mx-auto max-w-3xl px-8 lg:px-0${containerClassName ? ` ${containerClassName}` : ""}`}>
        <h2 className="mb-8 text-4xl font-bold md:mb-16 lg:text-5xl">{title}</h2>

        {groups.map((group, gIndex) => (
          <div className={gIndex === 0 ? "" : "mt-6"} key={gIndex}>
            <h3 className="mb-6 text-lg font-medium">{group.heading}</h3>
            <div data-rounded="full" className="grid grid-cols-2 gap-4 border-t py-6 md:grid-cols-4">
              {group.members.map((member, mIndex) => (
                <div key={mIndex}>
                  <div className="bg-background size-20 rounded-full border p-0.5 shadow shadow-zinc-950/5">
                    <Image
                      src={member.avatar.src}
                      alt={member.avatar.alt}
                      width={member.avatar.width}
                      height={member.avatar.height}
                      priority={member.avatar.priority}
                      quality={member.avatar.quality}
                      sizes={member.avatar.sizes}
                      fill={member.avatar.fill}
                      className={`${member.avatar.className ? member.avatar.className + " " : ""}aspect-square rounded-full object-cover`}
                      placeholder={member.avatar.placeholder}
                    />
                  </div>
                  <span className="mt-2 block text-sm">{member.name}</span>
                  <span className="text-muted-foreground block text-xs">{member.role}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default TeamSection;
