"use client";

import React, { useState } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Banner1Props {
  title: string;
  description: string;
  linkText: string;
  linkUrl: string;
  linkTarget?: "_blank" | "_self";
  linkRel?: string;
  defaultVisible?: boolean;
  bgImage?: ImageProps;
  bgColor?: string;
}

const Banner1: React.FC<Banner1Props> = ({
  title,
  description,
  linkText,
  linkUrl,
  linkTarget,
  linkRel,
  defaultVisible,
  bgImage,
  bgColor,
}) => {
  const [isVisible, setIsVisible] = useState<boolean>(defaultVisible ?? true);

  const handleClose = () => {
    setIsVisible(false);
  };

  if (!isVisible) return null;

  return (
    <section
      className="relative bg-background w-full border-b px-4 py-3"
      style={bgColor ? { backgroundColor: bgColor } : undefined}
    >
      {bgImage ? (
        <Image
          {...bgImage}
          className={[
            "absolute inset-0 -z-10 object-cover",
            bgImage.className || "",
          ]
            .filter(Boolean)
            .join(" ")}
        />
      ) : null}
      <div className="flex items-center justify-between gap-2">
        <div className="flex-1 text-center">
          <span className="text-sm">
            <span className="font-medium">{title}</span>{" "}
            <span className="text-muted-foreground">
              {description}{" "}
              <Button asChild variant="link" className="p-0 h-auto">
                <Link
                  href={linkUrl}
                  className="hover:text-foreground underline underline-offset-2"
                  target={linkTarget}
                  rel={linkRel}
                >
                  {linkText}
                </Link>
              </Button>
              .
            </span>
          </span>
        </div>

        <Button
          variant="ghost"
          size="icon"
          className="-mr-2 h-8 w-8 flex-none"
          onClick={handleClose}
          aria-label="Close"
        >
          <DynamicIcon name="X" className="h-4 w-4" />
        </Button>
      </div>
    </section>
  );
};

export { Banner1 };

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }
