"use client";

import { useState } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";

interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Banner2Props {
  title: string;
  description: string;
  linkText: string;
  linkUrl: string;
  linkTarget?: "_blank" | "_self";
  linkRel?: string;
  defaultVisible?: boolean;
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
}

const Banner2 = ({
  title,
  description,
  linkText,
  linkUrl,
  linkTarget,
  linkRel,
  defaultVisible,
  backgroundColor,
  backgroundImage,
}: Banner2Props) => {
  const [isVisible, setIsVisible] = useState<boolean>(
    defaultVisible !== undefined ? defaultVisible : true
  );

  const handleClose = () => {
    setIsVisible(false);
  };

  if (!isVisible) return null;

  return (
    <section
      className="relative w-full px-4 py-3"
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="pointer-events-none absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={`object-cover ${backgroundImage.className ?? ""}`}
          />
        </div>
      ) : (
        <div className="absolute inset-0 -z-10 bg-muted" />
      )}
      <div className="container">
        <div className="flex items-center gap-2">
          <div className="flex-1">
            <span className="text-sm">
              <span className="font-medium">{title}</span>{" "}
              <span className="text-muted-foreground">
                {description}{" "}
                <Link
                  href={linkUrl}
                  className="hover:text-foreground underline underline-offset-2"
                  target={linkTarget}
                  rel={linkRel}
                >
                  {linkText}
                </Link>
                .
              </span>
            </span>
          </div>

          <Button
            variant="ghost"
            size="icon"
            className="-mr-2 h-8 w-8 flex-none"
            onClick={handleClose}
            aria-label="Close banner"
          >
            <DynamicIcon name="X" className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </section>
  );
};

export { Banner2 };
