import { Badge } from "@/components/ui/badge";
import Link from "@/components/Link";

export interface FaqItem {
  question: string;
  answer: string;
}

export interface Faq5Props {
  badge?: string;
  heading?: string;
  description?: string;
  faqs?: FaqItem[];
  backgroundImage?: string;
  backgroundColor?: string;
}

const Faq5 = ({
  badge,
  heading,
  description,
  faqs,
  backgroundImage,
  backgroundColor,
}: Faq5Props) => {
  return (
    <section className="py-32" style={{ backgroundImage: `url(${backgroundImage})`, backgroundColor }}>
      <div className="container">
        <div className="text-center">
          <Badge className="text-xs font-medium">{badge}</Badge>
          <h1 className="mt-4 text-4xl font-semibold">{heading}</h1>
          <p className="mt-6 font-medium text-muted-foreground">{description}</p>
        </div>
        <div className="mx-auto mt-14 max-w-screen-sm">
          {faqs?.map((faq, index) => (
            <div key={index} className="mb-8 flex gap-4">
              <span className="flex size-6 shrink-0 items-center justify-center rounded-sm bg-secondary font-mono text-xs text-primary">
                {index + 1}
              </span>
              <div>
                <div className="mb-2 flex items-center justify-between">
                  <h3 className="font-medium">{faq.question}</h3>
                </div>
                <p className="text-sm text-muted-foreground">{faq.answer}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Faq5;
