import { FC } from 'react';
import { FaSomeIcon } from 'react-icons/fa'; // Example icon import

interface ButtonProps {
  label: string;
  href: string;
}

interface Feature2Props {
  title: string;
  description?: string;
  imageSrc: string;
  imageAlt: string;
  buttonPrimary: ButtonProps;
  buttonSecondary: ButtonProps;
}

export const Feature2: FC<Feature2Props> = ({
  title = "Blocks built with Shadcn & Tailwind",
  description = "Hundreds of finely crafted components built with React, Tailwind and Shadcn UI. Developers can copy and paste these blocks directly into their project.",
  imageSrc = "https://shadcnblocks.com/images/block/placeholder-1.svg",
  imageAlt = "placeholder hero",
  buttonPrimary,
  buttonSecondary,
}) => {
  return (
    <section className="py-32">
      <div className="container">
        <div className="grid items-center gap-8 md:gap-16 lg:grid-cols-2">
          <img
            src={imageSrc}
            alt={imageAlt}
            className="max-h-96 w-full rounded-md object-cover"
          />
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">
              {title}
            </h1>
            <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">
              {description}
            </p>
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <a href={buttonPrimary.href} target="_blank" rel="noopener noreferrer" className="btn">{buttonPrimary.label}</a>
              <a href={buttonSecondary.href} target="_blank" rel="noopener noreferrer" className="btn btn-outline">{buttonSecondary.label}</a>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
