"use client";

import React, { useEffect, useState } from "react";
import { motion, useMotionValue } from "framer-motion";

import { cn } from "@/lib/utils";
import Image, { ImageProps as AppImageProps } from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

export type Direction = "left" | "right";

export interface PhotoItemData {
  id: string | number;
  order: number;
  x: string; // e.g., "-320px"
  y: string; // e.g., "15px"
  zIndex: number;
  direction: Direction;
  src: string;
  alt: string;
  width: number;
  height: number;
}

export interface PhotoGalleryProps {
  animationDelay: number;
  subtitle: string;
  titlePrefix: string; // Text before the highlighted span
  titleHighlight: string; // Text inside highlighted span
  photos: PhotoItemData[];
  ctaLabel: string;
  ctaHref: string;
  ctaTarget?: "_blank" | "_self";
  backgroundImage?: AppImageProps; // Optional background image
  backgroundColor?: string; // Optional background color hex/rgb/tailwind-compatible string
  className?: string;
}

export const PhotoGallery: React.FC<PhotoGalleryProps> = ({
  animationDelay,
  subtitle,
  titlePrefix,
  titleHighlight,
  photos,
  ctaLabel,
  ctaHref,
  ctaTarget,
  backgroundImage,
  backgroundColor,
  className,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const visibilityTimer = setTimeout(() => {
      setIsVisible(true);
    }, animationDelay * 1000);

    const animationTimer = setTimeout(() => {
      setIsLoaded(true);
    }, (animationDelay + 0.4) * 1000);

    return () => {
      clearTimeout(visibilityTimer);
      clearTimeout(animationTimer);
    };
  }, [animationDelay]);

  const containerVariants = {
    hidden: { opacity: 1 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.1,
      },
    },
  } as const;

  const photoVariants = {
    hidden: () => ({
      x: 0,
      y: 0,
      rotate: 0,
      scale: 1,
    }),
    visible: (custom: { x: string; y: string; order: number }) => ({
      x: custom.x,
      y: custom.y,
      rotate: 0,
      scale: 1,
      transition: {
        type: "spring",
        stiffness: 70,
        damping: 12,
        mass: 1,
        delay: custom.order * 0.15,
      },
    }),
  } as const;

  return (
    <div
      className={cn("mt-40 relative", className)}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={cn("h-full w-full object-cover", backgroundImage.className)}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      )}

      <div className="absolute inset-0 max-md:hidden top-[200px] -z-10 h-[300px] w-full bg-transparent bg-[linear-gradient(to_right,#57534e_1px,transparent_1px),linear-gradient(to_bottom,#57534e_1px,transparent_1px)] bg-[size:3rem_3rem] opacity-20 [mask-image:radial-gradient(ellipse_80%_50%_at_50%_0%,#000_70%,transparent_110%)] dark:bg-[linear-gradient(to_right,#a8a29e_1px,transparent_1px),linear-gradient(to_bottom,#a8a29e_1px,transparent_1px)]" />

      <p className="lg:text-md my-2 text-center text-xs font-light uppercase tracking-widest text-slate-600 dark:text-slate-400">
        {subtitle}
      </p>
      <h3 className="z-20 mx-auto max-w-2xl justify-center bg-gradient-to-r from-slate-900 via-slate-800 to-slate-900 bg-clip-text py-3 text-center text-4xl text-transparent dark:bg-gradient-to-r dark:from-slate-100 dark:via-slate-200 dark:to-slate-100 dark:bg-clip-text md:text-7xl">
        {titlePrefix} <span className="text-rose-500">{titleHighlight}</span>
      </h3>

      <div className="relative mb-8 h-[350px] w-full items-center justify-center lg:flex">
        <motion.div
          className="relative mx-auto flex w-full max-w-7xl justify-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: isVisible ? 1 : 0 }}
          transition={{ duration: 0.4, ease: "easeOut" }}
        >
          <motion.div
            className="relative flex w-full justify-center"
            variants={containerVariants}
            initial="hidden"
            animate={isLoaded ? "visible" : "hidden"}
          >
            <div className="relative h-[220px] w-[220px]">
              {[...photos]
                .sort((a, b) => a.order - b.order)
                .reverse()
                .map((photo) => (
                  <motion.div
                    key={photo.id}
                    className="absolute left-0 top-0"
                    style={{ zIndex: photo.zIndex }}
                    variants={photoVariants}
                    custom={{ x: photo.x, y: photo.y, order: photo.order }}
                  >
                    <Photo
                      width={photo.width}
                      height={photo.height}
                      src={photo.src}
                      alt={photo.alt}
                      direction={photo.direction}
                    />
                  </motion.div>
                ))}
            </div>
          </motion.div>
        </motion.div>
      </div>

      <div className="flex w-full justify-center">
        <Button asChild>
          <Link href={ctaHref} target={ctaTarget} rel={ctaTarget === "_blank" ? "noopener noreferrer" : undefined} className="px-6 py-2">
            {ctaLabel}
          </Link>
        </Button>
      </div>
    </div>
  );
};

export interface PhotoProps {
  src: string;
  alt: string;
  className?: string;
  direction: Direction;
  width: number;
  height: number;
}

export const Photo: React.FC<PhotoProps> = ({
  src,
  alt,
  className,
  direction,
  width,
  height,
}) => {
  const [rotation, setRotation] = useState<number>(0);
  const x = useMotionValue(200);
  const y = useMotionValue(200);

  useEffect(() => {
    const randomRotation = getRandomNumberInRange(1, 4) * (direction === "left" ? -1 : 1);
    setRotation(randomRotation);
  }, [direction]);

  function handleMouse(event: React.MouseEvent<HTMLDivElement, MouseEvent>) {
    const rect = event.currentTarget.getBoundingClientRect();
    x.set(event.clientX - rect.left);
    y.set(event.clientY - rect.top);
  }

  const resetMouse = () => {
    x.set(200);
    y.set(200);
  };

  return (
    <motion.div
      drag
      dragConstraints={{ left: 0, right: 0, top: 0, bottom: 0 }}
      whileTap={{ scale: 1.2, zIndex: 9999 }}
      whileHover={{ scale: 1.1, rotateZ: 2 * (direction === "left" ? -1 : 1), zIndex: 9999 }}
      whileDrag={{ scale: 1.1, zIndex: 9999 }}
      initial={{ rotate: 0 }}
      animate={{ rotate: rotation }}
      style={{
        width,
        height,
        perspective: 400,
        transform: `rotate(0deg) rotateX(0deg) rotateY(0deg)`,
        zIndex: 1,
        WebkitTouchCallout: "none",
        WebkitUserSelect: "none",
        userSelect: "none",
        touchAction: "none",
      }}
      className={cn(className, "relative mx-auto shrink-0 cursor-grab active:cursor-grabbing")}
      onMouseMove={handleMouse}
      onMouseLeave={resetMouse}
      draggable={false}
      tabIndex={0}
    >
      <div className="relative h-full w-full overflow-hidden rounded-3xl shadow-sm">
        <Image
          className={cn("rounded-3xl object-cover")}
          src={src}
          alt={alt}
          width={width}
          height={height}
          draggable={false as unknown as undefined}
        />
      </div>
    </motion.div>
  );
};

function getRandomNumberInRange(min: number, max: number): number {
  if (min >= max) {
    throw new Error("Min value should be less than max value");
  }
  return Math.random() * (max - min) + min;
}

export default PhotoGallery;
