import React from "react";
import { cn } from "@/lib/utils";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

// Background image props interface aligned with project Image component guidelines
export interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface FeatureCardProps extends React.HTMLAttributes<HTMLDivElement> {
  title: string;
  description: string;
  iconName: string; // lucide icon name
  ctaLabel?: string;
  ctaHref?: string;
  ctaTarget?: "_blank" | "_self";
  backgroundImage?: BackgroundImageProps;
  backgroundColor?: string;
}

export function FeatureCard({
  title,
  description,
  iconName,
  ctaLabel,
  ctaHref,
  ctaTarget,
  backgroundImage,
  backgroundColor,
  className,
  style,
  ...rest
}: FeatureCardProps) {
  const p = genRandomPattern();
  const mergedStyle = backgroundColor ? { ...(style || {}), backgroundColor } : style;

  return (
    <div className={cn("relative overflow-hidden p-6", className)} style={mergedStyle} {...rest}>
      {backgroundImage && (
        <Image
          {...backgroundImage}
          className={cn("absolute inset-0 -z-10 object-cover", backgroundImage.className)}
        />
      )}

      <div className="pointer-events-none absolute top-0 left-1/2 -mt-2 -ml-20 h-full w-full [mask-image:linear-gradient(white,transparent)]">
        <div className="from-foreground/5 to-foreground/1 absolute inset-0 bg-gradient-to-r [mask-image:radial-gradient(farthest-side_at_top,white,transparent)] opacity-100">
          <GridPattern
            width={20}
            height={20}
            x="-12"
            y="4"
            squares={p}
            className="fill-foreground/5 stroke-foreground/25 absolute inset-0 h-full w-full mix-blend-overlay"
          />
        </div>
      </div>

      <DynamicIcon name={iconName} className="text-foreground/75 size-6" />
      <h3 className="mt-10 text-sm md:text-base">{title}</h3>
      <p className="text-muted-foreground relative z-20 mt-2 text-xs font-light">{description}</p>

      {ctaLabel && ctaHref ? (
        <div className="mt-4">
          <Button asChild>
            <Link
              href={ctaHref}
              target={ctaTarget}
              rel={ctaTarget === "_blank" ? "noopener noreferrer" : undefined}
              className="inline-flex items-center"
            >
              {ctaLabel}
            </Link>
          </Button>
        </div>
      ) : null}
    </div>
  );
}

function GridPattern({
  width,
  height,
  x,
  y,
  squares,
  ...props
}: React.ComponentProps<"svg"> & { width: number; height: number; x: string; y: string; squares?: number[][] }) {
  const patternId = React.useId();

  return (
    <svg aria-hidden="true" {...props}>
      <defs>
        <pattern id={patternId} width={width} height={height} patternUnits="userSpaceOnUse" x={x} y={y}>
          <path d={`M.5 ${height}V.5H${width}`} fill="none" />
        </pattern>
      </defs>
      <rect width="100%" height="100%" strokeWidth={0} fill={`url(#${patternId})`} />
      {squares && (
        <svg x={x} y={y} className="overflow-visible">
          {squares.map(([sx, sy], index) => (
            <rect
              strokeWidth="0"
              key={index}
              width={width + 1}
              height={height + 1}
              x={sx * width}
              y={sy * height}
            />
          ))}
        </svg>
      )}
    </svg>
  );
}

function genRandomPattern(length?: number): number[][] {
  const len = length ?? 5;
  return Array.from({ length: len }, () => [
    Math.floor(Math.random() * 4) + 7,
    Math.floor(Math.random() * 6) + 1,
  ]);
}

export default FeatureCard;
