"use client";

import AutoScroll from "embla-carousel-auto-scroll";

import {
  Carousel,
  CarouselContent,
  CarouselItem,
} from "@/components/ui/carousel";

import Image from "@/components/Image";

// Image interface per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface Logo {
  id: string;
  image: ImageProps;
}

export interface Logos3Props {
  heading: string;
  logos: Logo[];
  className?: string;
  backgroundImage?: ImageProps;
  backgroundColor?: string;
}

export const Logos3: React.FC<Logos3Props> = ({
  heading,
  logos,
  className,
  backgroundImage,
  backgroundColor,
}) => {
  return (
    <section
      className={`relative py-64 ${className ?? ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
      aria-label={heading}
    >
      {backgroundImage ? (
        <Image
          {...backgroundImage}
          fill
          className={`absolute inset-0 -z-10 object-cover ${backgroundImage.className ?? ""}`}
        />
      ) : null}

      <div className="container flex flex-col items-center text-center">
        <h1 className="my-6 text-2xl font-bold text-pretty lg:text-4xl">{heading}</h1>
      </div>

      <div className="pt-10 md:pt-16 lg:pt-20">
        <div className="relative mx-auto flex items-center justify-center lg:max-w-5xl">
          <Carousel opts={{ loop: true }} plugins={[AutoScroll({ playOnInit: true })]}>
            <CarouselContent className="ml-0">
              {logos.map((logo) => (
                <CarouselItem
                  key={logo.id}
                  className="flex basis-1/3 justify-center pl-0 sm:basis-1/4 md:basis-1/5 lg:basis-1/6"
                >
                  <div className="mx-10 flex shrink-0 items-center justify-center">
                    <div>
                      <Image {...logo.image} />
                    </div>
                  </div>
                </CarouselItem>
              ))}
            </CarouselContent>
          </Carousel>
          <div className="pointer-events-none absolute inset-y-0 left-0 w-12 bg-linear-to-r from-background to-transparent"></div>
          <div className="pointer-events-none absolute inset-y-0 right-0 w-12 bg-linear-to-l from-background to-transparent"></div>
        </div>
      </div>
    </section>
  );
};

export type { Logo };
