import { Star } from "lucide-react";
import React from "react";

import { Avatar, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import Link from "@/components/Link";

interface ReviewAvatar {
  src: string;
  alt: string;
}

interface ButtonProps {
  text: string;
  url: string;
}

interface Reviews {
  count: number;
  avatars: ReviewAvatar[];
}

interface Hero7Props {
  heading?: string;
  description?: string;
  button?: ButtonProps;
  reviews?: Reviews;
  backgroundImage?: string;
  backgroundColor?: string;
}

const Hero7 = ({
  heading,
  description,
  button,
  reviews,
  backgroundImage,
  backgroundColor,
}: Hero7Props) => {
  return (
    <section className="py-32" style={{ backgroundImage: backgroundImage, backgroundColor: backgroundColor }}>
      <div className="container text-center">
        <div className="mx-auto flex max-w-screen-lg flex-col gap-6">
          <h1 className="text-3xl font-extrabold lg:text-6xl">{heading}</h1>
          <p className="text-balance text-muted-foreground lg:text-lg">{description}</p>
        </div>
        <Button asChild size="lg" className="mt-10">
          <Link href={button?.url}>{button?.text}</Link>
        </Button>
        <div className="mx-auto mt-10 flex w-fit flex-col items-center gap-4 sm:flex-row">
          <span className="mx-4 inline-flex items-center -space-x-4">
            {reviews?.avatars.map((avatar, index) => (
              <Avatar key={index} className="size-14 border">
                <AvatarImage src={avatar.src} alt={avatar.alt} />
              </Avatar>
            ))}
          </span>
          <div>
            <div className="flex items-center gap-1">
              {[...Array(5)].map((_, index) => (
                <Star
                  key={index}
                  className="size-5 fill-yellow-400 text-yellow-400"
                />
              ))}
            </div>
            <p className="text-left font-medium text-muted-foreground">
              from {reviews?.count}+ reviews
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export { Hero7 };
