"use client";
import React from "react";
import { motion } from "framer-motion";
import Link from "@/components/Link";
import Image from "@/components/Image";

const transition = {
  type: "spring",
  mass: 0.5,
  damping: 11.5,
  stiffness: 100,
  restDelta: 0.001,
  restSpeed: 0.001,
} as const;

export interface MenuItemProps {
  setActive: (item: string) => void;
  active: string | null;
  item: string;
  children?: React.ReactNode;
  backgroundImageSrc?: string;
  backgroundColor?: string;
  className?: string;
}

export const MenuItem: React.FC<MenuItemProps> = ({
  setActive,
  active,
  item,
  children,
  backgroundImageSrc,
  backgroundColor,
  className,
}) => {
  return (
    <div
      onMouseEnter={() => setActive(item)}
      className={`relative ${className ?? ""}`.trim()}
      style={{
        backgroundColor: backgroundColor,
        backgroundImage: backgroundImageSrc ? `url(${backgroundImageSrc})` : undefined,
        backgroundSize: backgroundImageSrc ? "cover" : undefined,
        backgroundPosition: backgroundImageSrc ? "center" : undefined,
      }}
    >
      <motion.p
        transition={{ duration: 0.3 }}
        className="cursor-pointer text-black hover:opacity-[0.9] dark:text-white"
      >
        {item}
      </motion.p>
      {active !== null && (
        <motion.div
          initial={{ opacity: 0, scale: 0.85, y: 10 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          transition={transition}
        >
          {active === item && (
            <div className="absolute top-[calc(100%_+_1.2rem)] left-1/2 transform -translate-x-1/2 pt-4">
              <motion.div
                transition={transition}
                layoutId="active"
                className="bg-white dark:bg-black backdrop-blur-sm rounded-2xl overflow-hidden border border-black/[0.2] dark:border-white/[0.2] shadow-xl"
              >
                <motion.div layout className="w-max h-full p-4">
                  {children}
                </motion.div>
              </motion.div>
            </div>
          )}
        </motion.div>
      )}
    </div>
  );
};

export interface MenuProps {
  setActive: (item: string | null) => void;
  children: React.ReactNode;
  backgroundImageSrc?: string;
  backgroundColor?: string;
  className?: string;
}

export const Menu: React.FC<MenuProps> = ({
  setActive,
  children,
  backgroundImageSrc,
  backgroundColor,
  className,
}) => {
  return (
    <nav
      onMouseLeave={() => setActive(null)}
      className={`relative rounded-full border border-transparent dark:bg-black dark:border-white/[0.2] bg-white shadow-input flex justify-center space-x-4 px-8 py-6 ${className ?? ""}`.trim()}
      style={{
        backgroundColor: backgroundColor,
        backgroundImage: backgroundImageSrc ? `url(${backgroundImageSrc})` : undefined,
        backgroundSize: backgroundImageSrc ? "cover" : undefined,
        backgroundPosition: backgroundImageSrc ? "center" : undefined,
      }}
    >
      {children}
    </nav>
  );
};

export interface ProductItemProps {
  title: string;
  description: string;
  href: string;
  src: string;
  alt: string;
  width: number;
  height: number;
  target?: "_blank" | "_self";
  rel?: string;
  className?: string;
  imageClassName?: string;
}

export const ProductItem: React.FC<ProductItemProps> = ({
  title,
  description,
  href,
  src,
  alt,
  width,
  height,
  target,
  rel,
  className,
  imageClassName,
}) => {
  return (
    <Link href={href} target={target} rel={rel} className={`flex space-x-2 ${className ?? ""}`.trim()}>
      <Image
        src={src}
        width={width}
        height={height}
        alt={alt}
        className={`flex-shrink-0 rounded-md shadow-2xl ${imageClassName ?? ""}`.trim()}
      />
      <div>
        <h4 className="text-xl font-bold mb-1 text-black dark:text-white">{title}</h4>
        <p className="text-neutral-700 text-sm max-w-[10rem] dark:text-neutral-300">{description}</p>
      </div>
    </Link>
  );
};

export interface HoveredLinkProps {
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
  className?: string;
  children: React.ReactNode;
}

export const HoveredLink: React.FC<HoveredLinkProps> = ({ children, href, target, rel, className }) => {
  return (
    <Link
      href={href}
      target={target}
      rel={rel}
      className={`text-neutral-700 dark:text-neutral-200 hover:text-black ${className ?? ""}`.trim()}
    >
      {children}
    </Link>
  );
};

export default Menu;
