"use client";

import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import confetti from "canvas-confetti";
import NumberFlow from "@number-flow/react";

import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { cn } from "@/lib/utils";
import { useMediaQuery } from "@/hooks/use-media-query";

import Link from "@/components/Link";
import Image from "@/components/Image";
import DynamicIcon from "@/components/DynamicIcon";

export interface PricingPlan {
  name: string;
  price: number;
  yearlyPrice: number;
  period: string;
  features: string[];
  description: string;
  buttonText: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
  isPopular?: boolean;
  hidePeriodSuffix?: boolean;
}

export interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface PricingProps {
  plans: PricingPlan[];
  title: string;
  description: string;
  billingToggleLabel: string; // e.g., "Annual billing"
  billingToggleSavingLabel?: string; // e.g., "(Save 20%)"
  popularBadgeText: string; // e.g., "Popular"
  monthlyBillingText: string; // e.g., "billed monthly"
  annuallyBillingText: string; // e.g., "billed annually"
  currency: string; // e.g., "USD"
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
}

export function Pricing({
  plans,
  title,
  description,
  billingToggleLabel,
  billingToggleSavingLabel,
  popularBadgeText,
  monthlyBillingText,
  annuallyBillingText,
  currency,
  backgroundColor,
  backgroundImage,
}: PricingProps) {
  const [isMonthly, setIsMonthly] = useState(true);
  const isDesktop = useMediaQuery("(min-width: 768px)");
  const switchRef = useRef<HTMLButtonElement>(null);

  const handleToggle = (checked: boolean) => {
    setIsMonthly(!checked);
    if (checked && switchRef.current) {
      const rect = switchRef.current.getBoundingClientRect();
      const x = rect.left + rect.width / 2;
      const y = rect.top + rect.height / 2;

      confetti({
        particleCount: 50,
        spread: 60,
        origin: {
          x: x / window.innerWidth,
          y: y / window.innerHeight,
        },
        colors: [
          "hsl(var(--primary))",
          "hsl(var(--accent))",
          "hsl(var(--secondary))",
          "hsl(var(--muted))",
        ],
        ticks: 200,
        gravity: 1.2,
        decay: 0.94,
        startVelocity: 30,
        shapes: ["circle"],
      });
    }
  };

  return (
    <section
      className={cn("relative w-full overflow-hidden")}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <Image
          {...backgroundImage}
          className={cn("absolute inset-0 -z-10 h-full w-full object-cover", backgroundImage.className)}
        />
      ) : null}

      <div className="container relative z-10 py-20">
        <div className="text-center space-y-4 mb-12">
          <h2 className="text-4xl font-bold tracking-tight sm:text-5xl">{title}</h2>
          <p className="text-muted-foreground text-lg whitespace-pre-line">{description}</p>
        </div>

        <div className="flex justify-center items-center gap-2 mb-10">
          <label className="relative inline-flex items-center cursor-pointer">
            <Label>
              <Switch
                ref={switchRef}
                checked={!isMonthly}
                onCheckedChange={handleToggle}
                className="relative"
              />
            </Label>
          </label>
          <span className="ml-2 font-semibold">
            {billingToggleLabel} {" "}
            {billingToggleSavingLabel ? (
              <span className="text-primary">{billingToggleSavingLabel}</span>
            ) : null}
          </span>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 sm:2 gap-4">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name + index}
              initial={{ y: 50, opacity: 1 }}
              whileInView={
                isDesktop
                  ? {
                      y: plan.isPopular ? -20 : 0,
                      opacity: 1,
                      x: index === 2 ? -30 : index === 0 ? 30 : 0,
                      scale: index === 0 || index === 2 ? 0.94 : 1.0,
                    }
                  : {}
              }
              viewport={{ once: true }}
              transition={{
                duration: 1.6,
                type: "spring",
                stiffness: 100,
                damping: 30,
                delay: 0.4,
                opacity: { duration: 0.5 },
              }}
              className={cn(
                "rounded-2xl border-[1px] p-6 bg-background text-center lg:flex lg:flex-col lg:justify-center relative",
                plan.isPopular ? "border-primary border-2" : "border-border",
                "flex flex-col",
                !plan.isPopular && "mt-5",
                index === 0 || index === 2
                  ? "z-0 transform translate-x-0 translate-y-0 -translate-z-[50px] rotate-y-[10deg]"
                  : "z-10",
                index === 0 && "origin-right",
                index === 2 && "origin-left"
              )}
            >
              {plan.isPopular ? (
                <div className="absolute top-0 right-0 bg-primary py-0.5 px-2 rounded-bl-xl rounded-tr-xl flex items-center">
                  <DynamicIcon name="Star" className="text-primary-foreground h-4 w-4 fill-current" />
                  <span className="text-primary-foreground ml-1 font-sans font-semibold">
                    {popularBadgeText}
                  </span>
                </div>
              ) : null}

              <div className="flex-1 flex flex-col">
                <p className="text-base font-semibold text-muted-foreground">{plan.name}</p>
                <div className="mt-6 flex items-center justify-center gap-x-2">
                  <span className="text-5xl font-bold tracking-tight text-foreground">
                    <NumberFlow
                      value={isMonthly ? Number(plan.price) : Number(plan.yearlyPrice)}
                      format={{
                        style: "currency",
                        currency: currency,
                        minimumFractionDigits: 0,
                        maximumFractionDigits: 0,
                      }}
                      transformTiming={{ duration: 500, easing: "ease-out" }}
                      className="tabular-nums"
                    />
                  </span>
                  {!plan.hidePeriodSuffix ? (
                    <span className="text-sm font-semibold leading-6 tracking-wide text-muted-foreground">/ {plan.period}</span>
                  ) : null}
                </div>

                <p className="text-xs leading-5 text-muted-foreground">
                  {isMonthly ? monthlyBillingText : annuallyBillingText}
                </p>

                <ul className="mt-5 gap-2 flex flex-col">
                  {plan.features.map((feature, idx) => (
                    <li key={`${plan.name}-feature-${idx}`} className="flex items-start gap-2">
                      <DynamicIcon name="Check" className="h-4 w-4 text-primary mt-1 flex-shrink-0" />
                      <span className="text-left">{feature}</span>
                    </li>
                  ))}
                </ul>

                <hr className="w-full my-4" />

                <Button
                  asChild
                  variant={plan.isPopular ? "default" : "outline"}
                  className={cn(
                    "group relative w-full gap-2 overflow-hidden text-lg font-semibold tracking-tighter",
                    "transform-gpu ring-offset-current transition-all duration-300 ease-out hover:ring-2 hover:ring-primary hover:ring-offset-1"
                  )}
                >
                  <Link href={plan.href} target={plan.target} rel={plan.rel}>
                    {plan.buttonText}
                  </Link>
                </Button>

                <p className="mt-6 text-xs leading-5 text-muted-foreground">{plan.description}</p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
