import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";

// Icon interface as per guidelines
interface Icon {
  package: 'lucide';
  name: string; // known lucide react icon-name only
  type: 'icon';
  className: string; // tailwind classes for styling
}

// Image props interface as per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface FeatureItem {
  icon: Pick<Icon, 'name' | 'className'>;
  title: string;
  description: string;
}

export interface Hero45Props {
  badge?: string;
  heading: string;
  mainImage?: ImageProps;
  features?: FeatureItem[];
  backgroundColor?: string;
  backgroundImage?: ImageProps;
}

const Hero45: React.FC<Hero45Props> = ({
  badge,
  heading,
  mainImage,
  features,
  backgroundColor,
  backgroundImage,
}) => {
  return (
    <section className="py-32 relative" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={["h-full w-full object-cover", backgroundImage.className].filter(Boolean).join(" ")}
          />
        </div>
      )}
      <div className="container mx-auto overflow-hidden">
        <div className="mb-20 flex flex-col items-center gap-6 text-center">
          {badge && <Badge variant="outline">{badge}</Badge>}
          <h1 className="text-4xl font-semibold lg:text-5xl">{heading}</h1>
        </div>

        {mainImage && (
          <div className="relative mx-auto max-w-screen-lg">
            <Image
              {...mainImage}
              className={[
                "aspect-video max-h-[500px] w-full rounded-xl object-cover",
                mainImage.className,
              ].filter(Boolean).join(" ")}
            />
            <div className="absolute inset-0 bg-gradient-to-t from-background via-transparent to-transparent"></div>
            <div className="absolute -right-28 -top-28 -z-10 aspect-video h-72 w-96 opacity-40 [background-size:12px_12px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,#000_20%,transparent_100%)] sm:bg-[radial-gradient(hsl(var(--muted-foreground))_1px,transparent_1px)]"></div>
            <div className="absolute -left-28 -top-28 -z-10 aspect-video h-72 w-96 opacity-40 [background-size:12px_12px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,#000_20%,transparent_100%)] sm:bg-[radial-gradient(hsl(var(--muted-foreground))_1px,transparent_1px)]"></div>
          </div>
        )}

        {features && features.length > 0 && (
          <div className="mx-auto mt-10 flex max-w-screen-lg flex-col md:flex-row">
            {features.map((feature, index) => (
              <React.Fragment key={index}>
                {index > 0 && (
                  <Separator
                    orientation="vertical"
                    className="mx-6 hidden h-auto w-[2px] bg-gradient-to-b from-muted via-transparent to-muted md:block"
                  />
                )}
                <div className="flex grow basis-0 flex-col rounded-md bg-background p-4">
                  <div className="mb-6 flex size-10 items-center justify-center rounded-full bg-background drop-shadow-lg">
                    <DynamicIcon name={feature.icon.name} className={feature.icon.className} />
                  </div>
                  <h3 className="mb-2 font-semibold">{feature.title}</h3>
                  <p className="text-sm text-muted-foreground">{feature.description}</p>
                </div>
              </React.Fragment>
            ))}
          </div>
        )}
      </div>
    </section>
  );
};

export { Hero45 };
