import React, { useState } from 'react';
import { cn } from '@/lib/utils';
import { FaMinus, FaPlus } from 'react-icons/fa';

interface ComponentProps {
  title: string;
  initialCount?: number;
  href: string;
}

export const Component: React.FC<ComponentProps> = ({ title, initialCount = 0, href }) => {
  const [count, setCount] = useState(initialCount);

  return (
    <div className={cn('flex flex-col items-center gap-4 p-4 rounded-lg')}> 
      <h1 className="text-2xl font-bold mb-2">{title}</h1>
      <h2 className="text-xl font-semibold">{count}</h2>
      <div className="flex gap-2">
        <a href={href} className="text-lg p-2 bg-gray-200 rounded" onClick={() => setCount((prev) => prev - 1)}><FaMinus /></a>
        <a href={href} className="text-lg p-2 bg-gray-200 rounded" onClick={() => setCount((prev) => prev + 1)}><FaPlus /></a>
      </div>
    </div>
  );
};
