import { cn } from "@/lib/utils";
import { useState } from "react";
import Link from "@/components/Link";

interface CounterComponentProps {
    backgroundImage?: string;
    backgroundColor?: string;
    title: string;
    initialCount?: number;
    incrementLabel: string;
    decrementLabel: string;
}

export const CounterComponent: React.FC<CounterComponentProps> = ({
    backgroundImage,
    backgroundColor,
    title,
    initialCount = 0,
    incrementLabel,
    decrementLabel,
}) => {
    const [count, setCount] = useState(initialCount);

    return (
        <div className={cn("flex flex-col items-center gap-4 p-4 rounded-lg", backgroundColor, {"bg-cover": backgroundImage})} style={{ backgroundImage: backgroundImage ? `url(${backgroundImage})` : undefined }}>
            <h1 className="text-2xl font-bold mb-2">{title}</h1>
            <h2 className="text-xl font-semibold">{count}</h2>
            <div className="flex gap-2">
                <button onClick={() => setCount((prev) => prev - 1)}>
                    <Link href="#" className="text-blue-500">{decrementLabel}</Link>
                </button>
                <button onClick={() => setCount((prev) => prev + 1)}>
                    <Link href="#" className="text-blue-500">{incrementLabel}</Link>
                </button>
            </div>
        </div>
    );
};

export default CounterComponent;
