import * as React from "react";
import Link from "@/components/Link";
import Image from "@/components/Image";
import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

interface StatsCardProps {
  name: string;
  value: string;
  change: string;
  changeType: 'positive' | 'negative';
  href: string;
}

interface StatsProps {
  backgroundImage?: string;
  backgroundColor?: string;
  data: StatsCardProps[];
}

const Stats: React.FC<StatsProps> = ({ backgroundImage, backgroundColor, data }) => {
  return (
    <div className={`flex items-center justify-center p-10 w-full ${backgroundColor ? backgroundColor : ''}`} style={{ backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'none' }}>
      <dl className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3 w-full">
        {data.map((item) => (
          <div key={item.name} className="bg-card text-card-foreground flex flex-col gap-6 rounded-xl border py-6 shadow-sm p-0">
            <div className="px-6">
              <dd className="flex items-start justify-between space-x-2">
                <span className="truncate text-sm text-muted-foreground">
                  {item.name}
                </span>
                <span className={cn(
                  "text-sm font-medium",
                  item.changeType === "positive"
                    ? "text-emerald-700 dark:text-emerald-500"
                    : "text-red-700 dark:text-red-500"
                )}>
                  {item.change}
                </span>
              </dd>
              <dd className="mt-1 text-3xl font-semibold text-foreground">
                {item.value}
              </dd>
            </div>
            <div className="flex justify-end border-t border-border !p-0">
              <Link href={item.href} className="px-6 py-3 text-sm font-medium text-primary hover:text-primary/90">
                View more →
              </Link>
            </div>
          </div>
        ))}
      </dl>
    </div>
  );
};

export default Stats;
