import React from 'react';
import { FaStar } from 'react-icons/fa';

interface StatsSectionProps {
    title: string;
    description: string;
    stats: Array<{
        value: string;
        label: string;
    }>; 
    buttonText: string;
    buttonUrl: string;
}

const StatsSection: React.FC<StatsSectionProps> = ({ title, description, stats, buttonText, buttonUrl }) => {
    return (
        <section className="py-12 md:py-20">
            <div className="mx-auto max-w-5xl space-y-8 px-6 md:space-y-16">
                <div className="relative z-10 mx-auto max-w-xl space-y-6 text-center">
                    <h2 className="text-4xl font-medium lg:text-5xl">{title}</h2>
                    <p>{description}</p>
                </div>

                <div className="grid gap-12 divide-y *:text-center md:grid-cols-3 md:gap-2 md:divide-x md:divide-y-0">
                    {stats.map((stat, index) => (
                        <div key={index} className="space-y-4">
                            <div className="text-5xl font-bold">{stat.value}</div>
                            <p>{stat.label}</p>
                        </div>
                    ))}
                </div>
                <a href={buttonUrl} className="mt-6 inline-block rounded bg-blue-600 px-4 py-2 text-white hover:bg-blue-700">{buttonText}</a>
            </div>
        </section>
    );
};

export default StatsSection;
