import React from 'react';
import { FaStar } from 'react-icons/fa';

interface StatsSectionProps {
    title: string;
    description: string;
    stats: Array<{ value: string; label: string; }>;  
    href: string;
}

const StatsSection: React.FC<StatsSectionProps> = ({ title, description, stats, href }) => {
    return (
        <section className="py-12 md:py-20">
            <div className="mx-auto max-w-5xl space-y-8 px-6 md:space-y-16">
                <div className="relative z-10 mx-auto max-w-xl space-y-6 text-center">
                    <h2 className="text-4xl font-medium lg:text-5xl">{title}</h2>
                    <p>{description}</p>
                </div>

                <div className="grid gap-12 divide-y text-center md:grid-cols-3 md:gap-2 md:divide-x md:divide-y-0">
                    {stats.map((stat, index) => (
                        <div className="space-y-4" key={index}> 
                            <div className="text-5xl font-bold">{stat.value}</div>
                            <p>{stat.label}</p>
                        </div>
                    ))}
                </div>
                <a href={href} className="inline-block mt-8 text-lg font-semibold text-blue-500">
                    Learn More <FaStar />
                </a>
            </div>
        </section>
    );
};

export default StatsSection;
