import { MoveDownLeft, MoveUpRight } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import Link from "@/components/Link";

interface StatsProps {
  backgroundImage?: string;
  backgroundColor?: string;
  platformLabel: string;
  title: string;
  description: string;
  stats: Array<{
    icon: 'up' | 'down';
    value: string;
    change: string;
    label: string;
  }>;  
  buttonText: string;
  buttonLink: string;
}

function Stats({ backgroundImage, backgroundColor, platformLabel, title, description, stats, buttonText, buttonLink }: StatsProps) {
  return (
    <div className={`w-full py-20 lg:py-40 ${backgroundColor ? backgroundColor : ''}`} style={{ backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'none' }}>
      <div className="container mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-10">
          <div className="flex gap-4 flex-col items-start">
            <div>
              <Badge>{platformLabel}</Badge>
            </div>
            <div className="flex gap-2 flex-col">
              <h2 className="text-xl md:text-3xl md:text-5xl tracking-tighter lg:max-w-xl font-regular text-left">
                {title}
              </h2>
              <p className="text-lg lg:max-w-sm leading-relaxed tracking-tight text-muted-foreground text-left">
                {description}
              </p>
            </div>
          </div>
          <div className="flex justify-center items-center">
            <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 w-full gap-2">
              {stats.map((stat, index) => (
                <div key={index} className="flex gap-0 flex-col justify-between p-6 border rounded-md">
                  {stat.icon === 'up' ? <MoveUpRight className="w-4 h-4 mb-10 text-primary" /> : <MoveDownLeft className="w-4 h-4 mb-10 text-destructive" />}
                  <h2 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                    {stat.value}
                    <span className="text-muted-foreground text-sm tracking-normal">
                      {stat.change}
                    </span>
                  </h2>
                  <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                    {stat.label}
                  </p>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="flex justify-center mt-4">
          <Link href={buttonLink} className="your-tailwind-classes" target="_blank" rel="noopener noreferrer">
            <button>{buttonText}</button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export { Stats };
