import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import { Badge } from "@/components/ui/badge";

// Icon interface guideline
interface Icon {
  package: 'lucide';
  name: string; // known lucide react icon-name only
  type: 'icon';
  className: string; // tailwind classes for styling
}

// ImageProps guideline for optional background image
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface StatItem {
  icon: Icon;
  value: string; // e.g. "500.000" or "$523.520"
  change: string; // e.g. "+20.1%"
  subtitle: string; // e.g. "Monthly active users"
}

export interface StatsProps {
  badgeText: string;
  title: string;
  description: string;
  items: StatItem[];
  className?: string;
  itemCardClassName?: string;
  backgroundImage?: ImageProps; // optional background image
  backgroundColor?: string; // optional background color (e.g., hex, rgb, tailwind via inline style)
}

export function Stats({
  badgeText,
  title,
  description,
  items,
  className,
  itemCardClassName,
  backgroundImage,
  backgroundColor,
}: StatsProps) {
  return (
    <div
      className={`w-full py-20 lg:py-40 ${backgroundImage ? 'relative overflow-hidden' : ''} ${className ?? ''}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage && (
        <Image
          {...backgroundImage}
          className={`${backgroundImage.className ?? ''} absolute inset-0 -z-10 object-cover w-full h-full`}
        />
      )}
      <div className="container mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-10">
          <div className="flex gap-4 flex-col items-start">
            <div>
              <Badge>{badgeText}</Badge>
            </div>
            <div className="flex gap-2 flex-col">
              <h2 className="text-xl md:text-3xl md:text-5xl tracking-tighter lg:max-w-xl font-regular text-left">
                {title}
              </h2>
              <p className="text-lg lg:max-w-sm leading-relaxed tracking-tight text-muted-foreground text-left">
                {description}
              </p>
            </div>
          </div>
          <div className="flex justify-center items-center">
            <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 w-full gap-2">
              {items.map((item, idx) => (
                <div key={idx} className={`flex gap-0 flex-col justify-between p-6 border rounded-md ${itemCardClassName ?? ''}`}>
                  <DynamicIcon name={item.icon.name} className={item.icon.className} />
                  <h3 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                    {item.value}
                    <span className="text-muted-foreground text-sm tracking-normal">{item.change}</span>
                  </h3>
                  <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                    {item.subtitle}
                  </p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export { Stats };
