import { MoveDownLeft, MoveUpRight } from 'react-icons/lucide';

interface StatItem {
  value: string;
  percentage: string;
  description: string;
  icon: JSX.Element;
}

interface StatsProps {
  stats: StatItem[];
  linkHref: string;
}

const Stats: React.FC<StatsProps> = ({ stats, linkHref }) => {
  return (
    <div className="w-full py-20 lg:py-40">
      <div className="container mx-auto">
        <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 w-full gap-4 lg:gap-8">
          {stats.map((stat, index) => (
            <div key={index} className="flex gap-0 flex-col justify-between p-6 border rounded-md">
              {stat.icon}
              <h2 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                {stat.value}
                <span className="text-muted-foreground text-sm tracking-normal">
                  {stat.percentage}
                </span>
              </h2>
              <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                {stat.description}
              </p>
            </div>
          ))}
        </div>
      </div>
      <a href={linkHref} className="btn">View More</a>
    </div>
  );
};

export { Stats };
