import { MoveDownLeft, MoveUpRight } from "lucide-react";
import Link from '@/components/Link';

interface StatsProps {
  backgroundImage?: string;
  backgroundColor?: string;
  statsData: Array<{ 
    value: number | string; 
    change: number | string; 
    description: string; 
    icon: 'MoveUpRight' | 'MoveDownLeft';
    iconColor: string;
  }>;  
  buttonLink: string;
  buttonText: string;
}

function Stats({ backgroundImage, backgroundColor, statsData, buttonLink, buttonText }: StatsProps) {
  return (
    <div className={`w-full py-20 lg:py-40 ${backgroundColor ? backgroundColor : ''}`} style={{ backgroundImage: backgroundImage ? `url(${backgroundImage})` : undefined }}>
      <div className="container mx-auto">
        <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 w-full gap-4 lg:gap-8">
          {statsData.map((stat, index) => (
            <div key={index} className="flex gap-0 flex-col justify-between p-6 border rounded-md">
              {stat.icon === 'MoveUpRight' ? <MoveUpRight className={`w-4 h-4 mb-10 ${stat.iconColor}`} /> : <MoveDownLeft className={`w-4 h-4 mb-10 ${stat.iconColor}`} />}
              <h2 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                {stat.value}
                <span className="text-muted-foreground text-sm tracking-normal">
                  {stat.change}
                </span>
              </h2>
              <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                {stat.description}
              </p>
            </div>
          ))}
        </div>
        <Link href={buttonLink} className="mt-6 inline-flex items-center justify-center p-3 bg-blue-500 text-white rounded-md" target="_blank" rel="noopener noreferrer">
          {buttonText}
        </Link>
      </div>
    </div>
  );
}

export { Stats };
