"use client";
import React from "react";
import { motion } from "motion/react";
import Image from "@/components/Image";

export interface TestimonialsColumnProps {
  className?: string;
  duration?: number;
  testimonials: Array<{
    text: string;
    authorName: string;
    authorRole: string;
    authorAvatar: {
      src: string;
      alt: string;
      width: number;
      height: number;
      priority?: boolean;
      quality?: number;
      sizes?: string;
      fill?: boolean;
      className?: string;
      placeholder?: string;
    };
  }>;
  backgroundImage?: {
    src: string;
    alt: string;
    width: number;
    height: number;
    priority?: boolean;
    quality?: number;
    sizes?: string;
    fill?: boolean;
    className?: string;
    placeholder?: string;
  };
  backgroundColor?: string;
}

export const TestimonialsColumn: React.FC<TestimonialsColumnProps> = ({
  className,
  duration,
  testimonials,
  backgroundImage,
  backgroundColor,
}) => {
  return (
    <div className={className} style={backgroundColor ? { backgroundColor } : undefined}>
      <div className="relative">
        {backgroundImage ? (
          <Image
            {...backgroundImage}
            className={`absolute inset-0 -z-10 object-cover ${backgroundImage.className ?? ""}`}
          />
        ) : null}
        <motion.div
          animate={{ translateY: "-50%" }}
          transition={{ duration, repeat: Infinity, ease: "linear", repeatType: "loop" }}
          className="flex flex-col gap-6 pb-6"
        >
          {Array.from({ length: 2 }).map((_, index) => (
            <React.Fragment key={index}>
              {testimonials.map(({ text, authorAvatar, authorName, authorRole }, i) => (
                <div className="p-10 rounded-3xl border shadow-lg shadow-primary/10 max-w-xs w-full" key={i}>
                  <div>{text}</div>
                  <div className="flex items-center gap-2 mt-5">
                    <Image
                      {...authorAvatar}
                      className={`h-10 w-10 rounded-full ${authorAvatar.className ?? ""}`}
                    />
                    <div className="flex flex-col">
                      <div className="font-medium tracking-tight leading-5">{authorName}</div>
                      <div className="leading-5 opacity-60 tracking-tight">{authorRole}</div>
                    </div>
                  </div>
                </div>
              ))}
            </React.Fragment>
          ))}
        </motion.div>
      </div>
    </div>
  );
};

export default TestimonialsColumn;
