import * as React from "react";
import Link from "@/components/Link";
import DynamicIcon from "@/components/DynamicIcon";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";

export interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

export interface ContactAction {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface ContactInfoItem {
  title: string;
  description: string;
  icon: Icon;
  details: string[];
  actions?: ContactAction[];
}

export type ContactFormFieldType = "text" | "email" | "tel" | "textarea";

export interface ContactFormField {
  id: string;
  label: string;
  placeholder: string;
  type: ContactFormFieldType;
  required?: boolean;
  name?: string;
  rows?: number;
}

export interface ContactSections01Props {
  subheading?: string;
  heading?: string;
  description?: string;
  ctaLabel?: string;
  ctaHref?: string;
  ctaTarget?: "_blank" | "_self";
  ctaRel?: string;
  ctaIcon?: Icon;
  infoItems?: ContactInfoItem[];
  formBadge?: string;
  formHeading?: string;
  formDescription?: string;
  formFields?: ContactFormField[];
  submitButtonLabel?: string;
  privacyNote?: string;
  onSubmit?: (event: React.FormEvent<HTMLFormElement>) => void;
}

const defaultInfoItems: ContactInfoItem[] = [
  {
    title: "Sales & Partnerships",
    description: "Speak directly with our growth team.",
    icon: { package: "lucide", name: "Handshake", type: "icon" },
    details: ["sales@creativestudio.com", "+1 (555) 123-4567"],
    actions: [
      {
        label: "Book a call",
        href: "https://cal.com/creativestudio/intro-call",
        target: "_blank",
        rel: "noopener noreferrer",
      },
      {
        label: "Email sales",
        href: "mailto:sales@creativestudio.com",
      },
    ],
  },
  {
    title: "Support",
    description: "Need help with an existing project?",
    icon: { package: "lucide", name: "LifeBuoy", type: "icon" },
    details: ["support@creativestudio.com", "Mon - Fri · 9am to 6pm EST"],
  },
  {
    title: "Visit our studio",
    description: "Come by for a coffee and conversation.",
    icon: { package: "lucide", name: "MapPin", type: "icon" },
    details: ["200 Market Street, Suite 410", "San Francisco, CA 94105"],
  },
];

const defaultFormFields: ContactFormField[] = [
  {
    id: "fullName",
    name: "fullName",
    label: "Full name",
    placeholder: "Jane Cooper",
    type: "text",
    required: true,
  },
  {
    id: "email",
    name: "email",
    label: "Work email",
    placeholder: "jane@company.com",
    type: "email",
    required: true,
  },
  {
    id: "company",
    name: "company",
    label: "Company",
    placeholder: "Creative Studio Inc.",
    type: "text",
  },
  {
    id: "phone",
    name: "phone",
    label: "Phone number",
    placeholder: "+1 (555) 000-0000",
    type: "tel",
  },
  {
    id: "message",
    name: "message",
    label: "How can we help?",
    placeholder: "Tell us about your project goals and timelines.",
    type: "textarea",
    required: true,
    rows: 5,
  },
];

export default function ContactSections01({
  subheading = "Contact",
  heading = "Let's build something great together",
  description = "Share your product vision and we will follow up with a tailored plan, pricing, and next steps within one business day.",
  ctaLabel = "Schedule a consultation",
  ctaHref = "https://cal.com/creativestudio/strategy-call",
  ctaTarget = "_blank",
  ctaRel = "noopener noreferrer",
  ctaIcon = { package: "lucide", name: "ArrowUpRight", type: "icon" },
  infoItems = defaultInfoItems,
  formBadge = "Start a conversation",
  formHeading = "Tell us about your project",
  formDescription = "Fill out the form and our team will reach out with a personalized roadmap to help you move forward.",
  formFields = defaultFormFields,
  submitButtonLabel = "Send message",
  privacyNote = "We respect your privacy. Your information stays with us.",
  onSubmit,
}: ContactSections01Props) {
  const handleSubmit = React.useCallback(
    (event: React.FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      onSubmit?.(event);
    },
    [onSubmit],
  );

  return (
    <section className="w-full py-16">
      <div className="mx-auto flex w-full max-w-6xl flex-col gap-12 px-4 sm:px-6 lg:flex-row lg:gap-16">
        <div className="flex flex-1 flex-col gap-8">
          <div className="space-y-4">
            <span className="text-sm font-semibold uppercase tracking-wide text-primary">
              {subheading}
            </span>
            <h2 className="text-3xl font-bold tracking-tight text-foreground sm:text-4xl lg:text-5xl">
              {heading}
            </h2>
            <p className="max-w-2xl text-base text-muted-foreground sm:text-lg">
              {description}
            </p>
          </div>
          <div className="grid gap-4 sm:grid-cols-2">
            {infoItems.map((item) => (
              <div
                key={item.title}
                className="rounded-2xl border border-border bg-card p-6 shadow-sm transition hover:shadow-md"
              >
                <div className="flex items-start gap-4">
                  <span className="flex h-12 w-12 items-center justify-center rounded-full bg-primary/10 text-primary">
                    <DynamicIcon name={item.icon.name} className="h-5 w-5" />
                  </span>
                  <div className="flex flex-1 flex-col gap-3">
                    <div>
                      <h3 className="text-base font-semibold text-foreground">
                        {item.title}
                      </h3>
                      <p className="text-sm text-muted-foreground">
                        {item.description}
                      </p>
                    </div>
                    <ul className="space-y-1.5">
                      {item.details.map((detail) => (
                        <li key={detail} className="text-sm font-medium text-foreground">
                          {detail}
                        </li>
                      ))}
                    </ul>
                    {item.actions?.length ? (
                      <div className="flex flex-wrap gap-3 pt-2">
                        {item.actions.map((action) => (
                          <Button key={action.label} variant="outline" asChild>
                            <Link
                              href={action.href}
                              className="inline-flex items-center gap-2 text-sm font-semibold"
                              target={action.target}
                              rel={
                                action.rel ??
                                (action.target === "_blank" ? "noopener noreferrer" : undefined)
                              }
                            >
                              {action.label}
                            </Link>
                          </Button>
                        ))}
                      </div>
                    ) : null}
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div>
            <Button asChild>
              <Link
                href={ctaHref}
                className="inline-flex items-center gap-2 text-sm font-semibold"
                target={ctaTarget}
                rel={ctaRel}
              >
                <span>{ctaLabel}</span>
                {ctaIcon ? (
                  <DynamicIcon name={ctaIcon.name} className="h-4 w-4" />
                ) : null}
              </Link>
            </Button>
          </div>
        </div>
        <div className="flex flex-1 flex-col gap-6 rounded-3xl border border-border bg-card/70 p-8 shadow-lg">
          <div className="space-y-2">
            <span className="text-xs ust font-semibold uppercase tracking-wide text-primary">
              {formBadge}
            </span>
            <h3 className="text-2l font-semibold leading-tight text-foreground">
              {formHeading}
            </h3>
            <p className="text-sm text-muted-foreground">
              {formDescription}
            </p>
          </div>
          <form className="grid gap-5" onSubmit={handleSubmit}>
            {formFields.map((field) => (
              <div key={field.id} className="grid gap-2">
                <Label htmlFor={field.id} className="text-sm font-medium text-foreground">
                  {field.label}
                  {field.required ? <span className="ml-1 text-destructive">*</span> : null}
                </Label>
                {field.type === "textarea" ? (
                  <Textarea
                    id={field.id}
                    name={field.name ?? field.id}
                    placeholder={field.placeholder}
                    required={field.required}
                    rows={field.rows ?? 4}
                  />
                ) : (
                  <Input
                    id={field.id}
                    name={field.name ?? field.id}
                    type={field.type}
                    placeholder={field.placeholder}
                    required={field.required}
                  />
                )}
              </div>
            ))}
            <div className="flex flex-col gap-3">
              <button
                type="submit"
                className="inline-flex h-10 items-center justify-center rounded-md bg-primary px-6 text-sm font-semibold text-primary-foreground shadow transition-colors hover:bg-primary/90 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:pointer-events-none disabled:opacity-50"
              >
                {submitButtonLabel}
              </button>
              <p className="text-xs text-muted-foreground">{privacyNote}</p>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
}
