import * as React from "react";
import Link from "@/components/Link";
import DynamicIcon from "@/components/DynamicIcon";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

type LucideIconName = "Phone" | "Mail" | "MapPin" | "Clock";

export type ContactDetail = {
  icon: LucideIconName;
  label: string;
  value: string;
  href?: string;
  target?: "_blank" | "_self";
  rel?: string;
};

export type ContactFormLabels = {
  name: string;
  email: string;
  message: string;
  submit: string;
};

export type ContactFormPlaceholders = {
  name?: string;
  email?: string;
  message?: string;
};

export type ActionLink = {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
};

export type ContactFormValues = {
  name: string;
  email: string;
  message: string;
};

export interface ContactSections02Props {
  sectionId?: string;
  overline: string;
  title: string;
  description: string;
  contactDetails: ContactDetail[];
  primaryAction: ActionLink;
  secondaryAction?: ActionLink;
  formLabels: ContactFormLabels;
  formPlaceholders?: ContactFormPlaceholders;
  privacyNotice?: string;
  acknowledgementText?: string;
  onSubmit?: (values: ContactFormValues) => void;
}

const ContactSections02: React.FC<ContactSections02Props> = ({
  sectionId,
  overline,
  title,
  description,
  contactDetails,
  primaryAction,
  secondaryAction,
  formLabels,
  formPlaceholders,
  privacyNotice,
  acknowledgementText,
  onSubmit,
}) => {
  const [formValues, setFormValues] = React.useState<ContactFormValues>({
    name: "",
    email: "",
    message: "",
  });

  const handleInputChange = React.useCallback(
    (event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
      const { name, value } = event.target;
      setFormValues((prev) => {
        const field = name as keyof ContactFormValues;
        return {
          ...prev,
          [field]: value,
        };
      });
    },
    []
  );

  const handleSubmit = React.useCallback(
    (event: React.FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      onSubmit?.(formValues);
    },
    [formValues, onSubmit]
  );

  const resolveRel = React.useCallback(
    (target?: "_blank" | "_self", rel?: string) => {
      if (target === "_blank") {
        return rel ?? "noopener noreferrer";
      }
      return rel;
    },
    []
  );

  return (
    <section id={sectionId} className="w-full">
      <div className="mx-auto flex max-w-6xl flex-col gap-12 px-4 py-16 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 gap-10 lg:grid-cols-2">
          <div className="flex flex-col gap-6">
            <span className="text-sm font-semibold uppercase tracking-wide text-primary">
              {overline}
            </span>
            <h2 className="text-3xl font-semibold text-foreground sm:text-4xl">
              {title}
            </h2>
            <p className="text-base text-muted-foreground">
              {description}
            </p>
            <ul className="flex flex-col gap-4">
              {contactDetails.map((detail) => (
                <li
                  key={`${detail.label}-${detail.value}`}
                  className="flex items-start gap-3 rounded-xl border border-border p-4"
                >
                  <span className="flex h-10 w-10 items-center justify-center rounded-full bg-muted">
                    <DynamicIcon name={detail.icon} className="h-5 w-5 text-primary" />
                  </span>
                  <div className="flex flex-col">
                    <span className="text-sm font-semibold text-foreground">
                      {detail.label}
                    </span>
                    {detail.href ? (
                      <Link
                        href={detail.href}
                        className="text-base text-muted-foreground transition-colors hover:text-primary"
                        target={detail.target ?? "_self"}
                        rel={resolveRel(detail.target, detail.rel)}
                      >
                        {detail.value}
                      </Link>
                    ) : (
                      <span className="text-base text-muted-foreground">
                        {detail.value}
                      </span>
                    )}
                  </div>
                </li>
              ))}
            </ul>
            <div className="flex flex-wrap items-center gap-4">
              <Button asChild>
                <Link
                  href={primaryAction.href}
                  className="inline-flex items-center justify-center"
                  target={primaryAction.target ?? "_self"}
                  rel={resolveRel(primaryAction.target, primaryAction.rel)}
                >
                  {primaryAction.label}
                </Link>
              </Button>
              {secondaryAction ? (
                <Link
                  href={secondaryAction.href}
                  className="text-sm font-medium text-primary underline-offset-4 hover:underline"
                  target={secondaryAction.target ?? "_self"}
                  rel={resolveRel(secondaryAction.target, secondaryAction.rel)}
                >
                  {secondaryAction.label}
                </Link>
              ) : null}
            </div>
            {acknowledgementText ? (
              <p className="text-sm text-muted-foreground">
                {acknowledgementText}
              </p>
            ) : null}
          </div>
          <div className="rounded-2xl border border-border p-6 shadow-sm">
            <form className="flex flex-col gap-5" onSubmit={handleSubmit}>
              <label className="flex flex-col gap-2">
                <span className="text-sm font-medium text-foreground">
                  {formLabels.name}
                </span>
                <Input
                  name="name"
                  value={formValues.name}
                  onChange={handleInputChange}
                  placeholder={formPlaceholders?.name}
                  required
                />
              </label>
              <label className="flex flex-col gap-2">
                <span className="text-sm font-medium text-foreground">
                  {formLabels.email}
                </span>
                <Input
                  type="email"
                  name="email"
                  value={formValues.email}
                  onChange={handleInputChange}
                  placeholder={formPlaceholders?.email}
                  required
                />
              </label>
              <label className="flex flex-col gap-2">
                <span className="text-sm font-medium text-foreground">
                  {formLabels.message}
                </span>
                <Textarea
                  name="message"
                  value={formValues.message}
                  onChange={handleInputChange}
                  placeholder={formPlaceholders?.message}
                  rows={4}
                  required
                />
              </label>
              {privacyNotice ? (
                <p className="text-xs text-muted-foreground">
                  {privacyNotice}
                </p>
              ) : null}
              <Button type="submit">
                {formLabels.submit}
              </Button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

ContactSections02.displayName = "ContactSections02";

export default ContactSections02;
