import * as React from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import Link from "@/components/Link";
import Image from "@/components/Image";
import DynamicIcon from "@/components/DynamicIcon";

type LucideIconName = "Mail" | "Phone" | "MapPin" | "Clock" | "Linkedin" | "Twitter" | "Instagram";

interface ContactItem {
  label: string;
  value: string;
  iconName: LucideIconName;
  href?: string;
  target?: "_blank" | "_self";
}

interface SocialLink {
  label: string;
  href: string;
  iconName: LucideIconName;
  target?: "_blank" | "_self";
}

interface MediaProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
}

export interface ContactSections03Props {
  eyebrow?: string;
  heading: string;
  description: string;
  contactItems?: ContactItem[];
  socialLinks?: SocialLink[];
  formHeading: string;
  formDescription?: string;
  nameLabel: string;
  namePlaceholder: string;
  emailLabel: string;
  emailPlaceholder: string;
  messageLabel: string;
  messagePlaceholder: string;
  submitLabel: string;
  ctaLabel?: string;
  ctaHref?: string;
  ctaTarget?: "_blank" | "_self";
  onSubmit?: React.FormEventHandler<HTMLFormElement>;
  media?: MediaProps;
}

const defaultContactItems: ContactItem[] = [
  {
    label: "Email",
    value: "support@company.com",
    iconName: "Mail",
    href: "mailto:support@company.com",
    target: "_self",
  },
  {
    label: "Phone",
    value: "+1 (555) 123-4567",
    iconName: "Phone",
    href: "tel:+15551234567",
    target: "_self",
  },
  {
    label: "Office",
    value: "401 Broadway, 24th Floor, New York, NY",
    iconName: "MapPin",
  },
  {
    label: "Hours",
    value: "Mon - Fri, 9am - 6pm EST",
    iconName: "Clock",
  },
];

const defaultSocialLinks: SocialLink[] = [
  {
    label: "LinkedIn",
    href: "https://www.linkedin.com/company/yourcompany",
    iconName: "Linkedin",
    target: "_blank",
  },
  {
    label: "Twitter",
    href: "https://twitter.com/yourcompany",
    iconName: "Twitter",
    target: "_blank",
  },
  {
    label: "Instagram",
    href: "https://www.instagram.com/yourcompany",
    iconName: "Instagram",
    target: "_blank",
  },
];

const defaultMedia: MediaProps = {
  src: "/images/contact-map-placeholder.jpg",
  alt: "Company headquarters location map",
  width: 640,
  height: 480,
  className: "rounded-xl",
};

const ContactSections03: React.FC<ContactSections03Props> = ({
  eyebrow = "Contact",
  heading = "Let's build something together",
  description = "We would love to learn more about your goals. Share a few details and our team will reach out within one business day.",
  contactItems = defaultContactItems,
  socialLinks = defaultSocialLinks,
  formHeading = "Send us a message",
  formDescription = "Fill in the form and we will get back to you shortly.",
  nameLabel = "Full name",
  namePlaceholder = "Enter your full name",
  emailLabel = "Work email",
  emailPlaceholder = "you@company.com",
  messageLabel = "How can we help?",
  messagePlaceholder = "Tell us about your project...",
  submitLabel = "Submit inquiry",
  ctaLabel = "Book a discovery call",
  ctaHref = "https://calendly.com/yourcompany/discovery-call",
  ctaTarget = "_blank",
  onSubmit,
  media = defaultMedia,
}) => {
  const mediaClassName = media?.className
    ? `h-full w-full object-cover ${media.className}`
    : "h-full w-full object-cover";

  return (
    <section className="w-full">
      <div className="mx-auto grid max-w-6xl gap-12 px-6 py-16 lg:grid-cols-[1.05fr_0.95fr] lg:gap-16">
        <div className="flex flex-col gap-10">
          <div className="space-y-5">
            {eyebrow ? (
              <p className="text-sm font-semibold uppercase tracking-wide text-primary">
                {eyebrow}
              </p>
            ) : null}
            <div className="space-y-4">
              <h2 className="text-3xl font-semibold tracking-tight text-foreground sm:text-4xl">
                {heading}
              </h2>
              <p className="text-base leading-relaxed text-muted-foreground">
                {description}
              </p>
            </div>
            {ctaLabel && ctaHref ? (
              <Button asChild size="lg">
                <Link
                  href={ctaHref}
                  target={ctaTarget}
                  rel="noopener noreferrer"
                  className="inline-flex items-center justify-center"
                >
                  {ctaLabel}
                </Link>
              </Button>
            ) : null}
          </div>

          <div className="grid gap-6 sm:grid-cols-2">
            {contactItems.map((item) => (
              <div
                key={`${item.label}-${item.value}`}
                className="flex gap-4 rounded-xl border border-border p-5 shadow-sm"
              >
                <div className="flex h-12 w-12 items-center justify-center rounded-full bg-primary/10 text-primary">
                  <DynamicIcon name={item.iconName} className="h-5 w-5" />
                </div>
                <div className="space-y-1">
                  <p className="text-sm font-medium text-muted-foreground">
                    {item.label}
                  </p>
                  {item.href ? (
                    <Link
                      href={item.href}
                      target={item.target ?? "_self"}
                      rel="noopener noreferrer"
                      className="text-base font-semibold text-foreground hover:text-primary"
                    >
                      {item.value}
                    </Link>
                  ) : (
                    <p className="text-base font-semibold text-foreground">
                      {item.value}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>

          {media?.src ? (
            <div className="overflow-hidden rounded-2xl border border-border">
              <Image
                src={media.src}
                alt={media.alt}
                width={media.width}
                height={media.height}
                className={mediaClassName}
              />
            </div>
          ) : null}

          {socialLinks.length ? (
            <div className="flex flex-wrap items-center gap-4">
              <p className="text-sm font-medium text-muted-foreground">
                Connect with us
              </p>
              <div className="flex items-center gap-3">
                {socialLinks.map((social) => (
                  <Link
                    key={social.label}
                    href={social.href}
                    target={social.target ?? "_blank"}
                    rel="noopener noreferrer"
                    className="inline-flex h-10 w-10 items-center justify-center rounded-full border border-border text-muted-foreground transition-colors hover:border-primary hover:text-primary"
                  >
                    <span className="sr-only">{social.label}</span>
                    <DynamicIcon name={social.iconName} className="h-5 w-5" />
                  </Link>
                ))}
              </div>
            </div>
          ) : null}
        </div>

        <div className="rounded-2xl border border-border bg-card p-8 shadow-md">
          <div className="space-y-3">
            <h3 className="text-2xl font-semibold text-foreground">
              {formHeading}
            </h3>
            {formDescription ? (
              <p className="text-sm text-muted-foreground">
                {formDescription}
              </p>
            ) : null}
          </div>
          <form
            className="mt-8 grid gap-6"
            onSubmit={onSubmit}
            noValidate
          >
            <div className="grid gap-2">
              <Label htmlFor="contact-name">{nameLabel}</Label>
              <Input
                id="contact-name"
                name="name"
                placeholder={namePlaceholder}
                required
                autoComplete="name"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="contact-email">{emailLabel}</Label>
              <Input
                id="contact-email"
                name="email"
                type="email"
                placeholder={emailPlaceholder}
                required
                autoComplete="email"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="contact-message">{messageLabel}</Label>
              <Textarea
                id="contact-message"
                name="message"
                placeholder={messagePlaceholder}
                rows={5}
                required
              />
            </div>
            <Button type="submit" size="lg" className="w-full">
              {submitLabel}
            </Button>
          </form>
        </div>
      </div>
    </section>
  );
};

export default ContactSections03;
