import type { ComponentPropsWithoutRef } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

interface FooterLinkItem {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FooterLinkColumn {
  title: string;
  links: FooterLinkItem[];
}

interface FooterSocialLink {
  label: string;
  href: string;
  icon: Icon;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FooterLegalLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FooterContactItem {
  label: string;
  value: string;
  href?: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FooterCta {
  label: string;
  href: string;
  variant?: "default" | "secondary" | "outline" | "ghost" | "link" | "destructive";
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Footers01Props extends Omit<ComponentPropsWithoutRef<"footer">, "children"> {
  brandName?: string;
  brandUrl?: string;
  tagline?: string;
  columns?: FooterLinkColumn[];
  socialLinks?: FooterSocialLink[];
  legalLinks?: FooterLegalLink[];
  legalNotice?: string;
  contactItems?: FooterContactItem[];
  primaryCta?: FooterCta | null;
}

const defaultBrandName = "Creative Tim";
const defaultBrandUrl = "/";
const defaultTagline = "Build modern UIs faster with production-ready components.";

const defaultFooterColumns: FooterLinkColumn[] = [
  {
    title: "Product",
    links: [
      { label: "Overview", href: "/product/overview" },
      { label: "Features", href: "/product/features" },
      { label: "Roadmap", href: "/product/roadmap" },
      { label: "Changelog", href: "/product/changelog" }
    ]
  },
  {
    title: "Company",
    links: [
      { label: "About", href: "/company/about" },
      { label: "Careers", href: "/company/careers" },
      { label: "Blog", href: "/company/blog" },
      { label: "Press", href: "/company/press" }
    ]
  },
  {
    title: "Resources",
    links: [
      { label: "Docs", href: "/resources/docs" },
      { label: "Community", href: "/resources/community" },
      { label: "Support", href: "/resources/support" },
      { label: "Contact", href: "/contact" }
    ]
  }
];

const defaultSocialLinks: FooterSocialLink[] = [
  {
    label: "Twitter",
    href: "https://twitter.com/creativetim",
    icon: { package: "lucide", name: "Twitter", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  },
  {
    label: "LinkedIn",
    href: "https://linkedin.com/company/creativetim",
    icon: { package: "lucide", name: "Linkedin", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  },
  {
    label: "GitHub",
    href: "https://github.com/creativetimofficial",
    icon: { package: "lucide", name: "Github", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  }
];

const defaultLegalLinks: FooterLegalLink[] = [
  { label: "Terms", href: "/legal/terms" },
  { label: "Privacy", href: "/legal/privacy" },
  { label: "Cookies", href: "/legal/cookies" }
];

const defaultContactItems: FooterContactItem[] = [
  {
    label: "Email",
    value: "hello@creativetim.com",
    href: "mailto:hello@creativetim.com"
  },
  {
    label: "Phone",
    value: "+1 (800) 555-0199",
    href: "tel:+18005550199"
  },
  {
    label: "Address",
    value: "111 Market Street, Suite 400, San Francisco, CA"
  }
];

const defaultPrimaryCta: FooterCta = {
  label: "Join the community",
  href: "/signup",
  variant: "default"
};

const defaultLegalNotice = "© 2024 Creative Tim. All rights reserved.";

export function Footers01({
  brandName = defaultBrandName,
  brandUrl = defaultBrandUrl,
  tagline = defaultTagline,
  columns,
  socialLinks,
  legalLinks,
  legalNotice = defaultLegalNotice,
  contactItems,
  primaryCta = defaultPrimaryCta,
  className,
  ...rest
}: Footers01Props) {
  const columnsToRender = columns && columns.length > 0 ? columns : defaultFooterColumns;
  const socialLinksToRender = socialLinks && socialLinks.length > 0 ? socialLinks : defaultSocialLinks;
  const legalLinksToRender = legalLinks && legalLinks.length > 0 ? legalLinks : defaultLegalLinks;
  const contactItemsToRender = contactItems && contactItems.length > 0 ? contactItems : defaultContactItems;
  const ctaToRender = primaryCta === null ? null : primaryCta ?? defaultPrimaryCta;

  return (
    <footer className={cn("w-full border-t border-border", className)} {...rest}>
      <div className="mx-auto flex max-w-7xl flex-col gap-12 px-4 py-12 sm:px-6 lg:px-8">
        <div className="grid gap-12 lg:grid-cols-[minmax(0,2fr)_minmax(0,3fr)]">
          <div className="flex flex-col gap-6">
            <Link href={brandUrl} className="text-2xl font-semibold tracking-tight text-foreground">
              {brandName}
            </Link>
            <p className="max-w-md text-sm leading-6 text-muted-foreground">{tagline}</p>
            {contactItemsToRender.length > 0 && (
              <ul className="flex flex-col gap-3 text-sm text-muted-foreground">
                {contactItemsToRender.map((item) => (
                  <li key={`${item.label}-${item.value}`} className="flex flex-col gap-1">
                    <span className="font-medium text-foreground">{item.label}</span>
                    {item.href ? (
                      <Link
                        href={item.href}
                        target={item.target}
                        rel={item.rel}
                        className="text-muted-foreground transition-colors hover:text-foreground"
                      >
                        {item.value}
                      </Link>
                    ) : (
                      <span className="text-muted-foreground">{item.value}</span>
                    )}
                  </li>
                ))}
              </ul>
            )}
            {ctaToRender && (
              <div>
                <Button asChild variant={ctaToRender.variant ?? "default"}>
                  <Link
                    href={ctaToRender.href}
                    target={ctaToRender.target}
                    rel={ctaToRender.rel}
                    className="inline-flex items-center justify-center"
                  >
                    {ctaToRender.label}
                  </Link>
                </Button>
              </div>
            )}
          </div>
          <div className="grid gap-8 sm:grid-cols-2 md:grid-cols-3">
            {columnsToRender.map((column) => (
              <div key={column.title} className="flex flex-col gap-4">
                <h3 className="text-sm font-semibold uppercase tracking-wide text-foreground">{column.title}</h3>
                <ul className="flex flex-col gap-3">
                  {column.links.map((link) => (
                    <li key={`${column.title}-${link.label}`}>
                      <Link
                        href={link.href}
                        target={link.target}
                        rel={link.rel}
                        className="text-sm text-muted-foreground transition-colors hover:text-foreground"
                      >
                        {link.label}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
        <div className="flex flex-col gap-6 border-t border-border pt-6 md:flex-row md:items-center md:justify-between">
          <div className="flex flex-wrap items-center gap-4">
            {socialLinksToRender.map((social) => (
              <Link
                key={social.label}
                href={social.href}
                target={social.target ?? "_blank"}
                rel={social.rel ?? "noopener noreferrer"}
                className="inline-flex h-10 w-10 items-center justify-center rounded-full border border-border transition-colors hover:border-primary hover:text-primary"
                aria-label={social.label}
              >
                <DynamicIcon name={social.icon.name} className="h-5 w-5" />
              </Link>
            ))}
          </div>
          <div className="flex flex-col gap-4 md:items-end">
            <div className="flex flex-wrap items-center gap-4">
              {legalLinksToRender.map((link) => (
                <Link
                  key={link.label}
                  href={link.href}
                  target={link.target}
                  rel={link.rel}
                  className="text-sm text-muted-foreground transition-colors hover:text-foreground"
                >
                  {link.label}
                </Link>
              ))}
            </div>
            <p className="text-sm text-muted-foreground">{legalNotice}</p>
          </div>
        </div>
      </div>
    </footer>
  );
}

export default Footers01;
