import * as React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

interface FooterNavLink {
  label: string;
  href: string;
}

interface FooterNavSection {
  title: string;
  links: FooterNavLink[];
}

interface FooterSocialLink {
  label: string;
  href: string;
  icon: Icon;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Footers02Props {
  brandName?: string;
  brandHref?: string;
  brandDescription?: string;
  navSections?: FooterNavSection[];
  socialLinks?: FooterSocialLink[];
  addressLines?: string[];
  contactTitle?: string;
  contactEmail?: string;
  contactPhone?: string;
  newsletterTitle?: string;
  newsletterDescription?: string;
  newsletterPlaceholder?: string;
  newsletterCtaHref?: string;
  newsletterCtaLabel?: string;
  policyLinks?: FooterNavLink[];
  copyright?: string;
}

const defaultNavSections: FooterNavSection[] = [
  {
    title: "Product",
    links: [
      { label: "Features", href: "/features" },
      { label: "Pricing", href: "/pricing" },
      { label: "Integrations", href: "/integrations" },
      { label: "Changelog", href: "/changelog" }
    ]
  },
  {
    title: "Company",
    links: [
      { label: "About", href: "/about" },
      { label: "Team", href: "/team" },
      { label: "Careers", href: "/careers" },
      { label: "Press", href: "/press" }
    ]
  },
  {
    title: "Resources",
    links: [
      { label: "Blog", href: "/blog" },
      { label: "Guides", href: "/guides" },
      { label: "Support", href: "/support" },
      { label: "Community", href: "/community" }
    ]
  }
];

const defaultSocialLinks: FooterSocialLink[] = [
  {
    label: "Follow us on Twitter",
    href: "https://twitter.com/yourbrand",
    icon: { package: "lucide", name: "Twitter", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  },
  {
    label: "Connect on LinkedIn",
    href: "https://linkedin.com/company/yourbrand",
    icon: { package: "lucide", name: "Linkedin", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  },
  {
    label: "Follow on Instagram",
    href: "https://instagram.com/yourbrand",
    icon: { package: "lucide", name: "Instagram", type: "icon" },
    target: "_blank",
    rel: "noopener noreferrer"
  }
];

const defaultAddressLines: string[] = [
  "123 Market Street",
  "San Francisco, CA 94103"
];

const defaultPolicyLinks: FooterNavLink[] = [
  { label: "Privacy Policy", href: "/privacy" },
  { label: "Terms of Service", href: "/terms" },
  { label: "Cookie Policy", href: "/cookies" }
];

const Footers02: React.FC<Footers02Props> = ({
  brandName = "Creative Agency",
  brandHref = "/",
  brandDescription = "Designing digital products that move culture and drive results.",
  navSections = defaultNavSections,
  socialLinks = defaultSocialLinks,
  addressLines = defaultAddressLines,
  contactTitle = "Contact",
  contactEmail = "hello@creativeagency.com",
  contactPhone = "+1 (555) 123-4567",
  newsletterTitle = "Stay in the loop",
  newsletterDescription = "Get updates, resources, and curated stories every month.",
  newsletterPlaceholder = "Enter your email",
  newsletterCtaHref = "/newsletter",
  newsletterCtaLabel = "Subscribe",
  policyLinks = defaultPolicyLinks,
  copyright
}) => {
  const computedCopyright =
    copyright ?? `© ${new Date().getFullYear()} ${brandName}. All rights reserved.`;

  return (
    <footer className="w-full">
      <div className="container space-y-12 py-16 md:space-y-16 md:py-24">
        <div className="grid gap-12 lg:grid-cols-[2fr_3fr]">
          <div className="space-y-8">
            <div className="space-y-4">
              <Link
                href={brandHref}
                className="inline-flex items-center text-2xl font-semibold tracking-tight text-foreground"
              >
                {brandName}
              </Link>
              <p className="max-w-md text-sm text-muted-foreground">{brandDescription}</p>
              <div className="flex items-center gap-3">
                {socialLinks.map((social) => (
                  <Link
                    key={social.href}
                    href={social.href}
                    target={social.target ?? "_blank"}
                    rel={social.rel ?? "noopener noreferrer"}
                    className="inline-flex h-10 w-10 items-center justify-center rounded-full border border-border text-muted-foreground transition hover:text-foreground"
                  >
                    <span className="sr-only">{social.label}</span>
                    <DynamicIcon name={social.icon.name} className="h-5 w-5" />
                  </Link>
                ))}
              </div>
            </div>
            <div className="space-y-4">
              <h3 className="text-sm font-semibold uppercase tracking-wide text-foreground">
                {newsletterTitle}
              </h3>
              <p className="text-sm text-muted-foreground">{newsletterDescription}</p>
              <div className="flex flex-col gap-3 sm:flex-row">
                <Input
                  type="email"
                  inputMode="email"
                  placeholder={newsletterPlaceholder}
                  className="sm:flex-1"
                  aria-label={newsletterPlaceholder}
                />
                <Button asChild className="sm:w-auto">
                  <Link
                    href={newsletterCtaHref}
                    className="inline-flex h-full w-full items-center justify-center px-4 py-2 text-sm font-medium"
                  >
                    {newsletterCtaLabel}
                  </Link>
                </Button>
              </div>
            </div>
            <div className="space-y-3">
              <h3 className="text-sm font-semibold uppercase tracking-wide text-foreground">
                {contactTitle}
              </h3>
              <ul className="space-y-1 text-sm text-muted-foreground">
                {addressLines.map((line) => (
                  <li key={line}>{line}</li>
                ))}
              </ul>
              <div className="flex flex-col gap-2 text-sm">
                <Link
                  href={`mailto:${contactEmail}`}
                  className="text-muted-foreground transition hover:text-foreground"
                >
                  {contactEmail}
                </Link>
                <Link
                  href={`tel:${contactPhone}`}
                  className="text-muted-foreground transition hover:text-foreground"
                >
                  {contactPhone}
                </Link>
              </div>
            </div>
          </div>
          <div className="grid grid-cols-2 gap-8 sm:grid-cols-3">
            {navSections.map((section) => (
              <div key={section.title} className="space-y-4">
                <h4 className="text-sm font-semibold uppercase tracking-wide text-foreground">
                  {section.title}
                </h4>
                <ul className="space-y-2 text-sm text-muted-foreground">
                  {section.links.map((link) => (
                    <li key={link.href}>
                      <Link
                        href={link.href}
                        className="transition hover:text-foreground"
                      >
                        {link.label}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
        <div className="flex flex-col gap-6 border-t border-border pt-8 text-sm text-muted-foreground lg:flex-row lg:items-center lg:justify-between">
          <div className="flex flex-wrap items-center gap-x-6 gap-y-2">
            {policyLinks.map((policy) => (
              <Link
                key={policy.href}
                href={policy.href}
                className="transition hover:text-foreground"
              >
                {policy.label}
              </Link>
            ))}
          </div>
          <p className="text-sm text-muted-foreground">{computedCopyright}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footers02;
