import * as React from "react";
import Link from "@/components/Link";
import Image from "@/components/Image";
import DynamicIcon from "@/components/DynamicIcon";
import { Button } from "@/components/ui/button";

export interface Footers03Props {
  logo: {
    src: string;
    alt: string;
    linkHref: string;
    text?: string;
  };
  description: string;
  navigation: Array<{
    title: string;
    items: Array<{
      label: string;
      href: string;
      target?: "_blank" | "_self";
    }>;
  }>;
  contact?: {
    heading: string;
    addressLines: string[];
    phone?: {
      label: string;
      href: string;
      target?: "_blank" | "_self";
    };
    email?: {
      label: string;
      href: string;
      target?: "_blank" | "_self";
    };
  };
  socialLinks?: Array<{
    label: string;
    href: string;
    iconName: React.ComponentProps<typeof DynamicIcon>["name"];
    target?: "_blank" | "_self";
  }>;
  cta?: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
  };
  bottomLinks?: Array<{
    label: string;
    href: string;
    target?: "_blank" | "_self";
  }>;
  copyright: string;
}

const Footers03: React.FC<Footers03Props> = ({
  logo,
  description,
  navigation,
  contact,
  socialLinks,
  cta,
  bottomLinks,
  copyright,
}) => {
  const resolveRel = (target?: "_blank" | "_self") =>
    target === "_blank" ? "noopener noreferrer" : undefined;

  return (
    <footer className="w-full">
      <div className="mx-auto flex w-full max-w-7xl flex-col gap-12 px-6 py-12 sm:gap-16 sm:px-8 md:px-12 lg:px-16">
        <div className="flex flex-col gap-10 md:flex-row md:justify-between md:gap-16">
          <div className="flex flex-col gap-6 md:max-w-sm">
            <Link
              href={logo.linkHref}
              className="flex items-center gap-2"
              target="_self"
            >
              <Image
                src={logo.src}
                alt={logo.alt}
                width={48}
                height={48}
                className="h-12 w-12 rounded-md object-contain"
                type="logo"
              />
              {logo.text ? (
                <span className="text-lg font-semibold text-foreground">
                  {logo.text}
                </span>
              ) : null}
            </Link>
            <p className="text-sm leading-relaxed text-muted-foreground">
              {description}
            </p>
            {cta ? (
              <Button asChild className="w-fit" size="sm">
                <Link
                  href={cta.href}
                  className="font-semibold"
                  target={cta.target ?? "_self"}
                  rel={resolveRel(cta.target)}
                >
                  {cta.label}
                </Link>
              </Button>
            ) : null}
          </div>
          <div className="flex flex-1 flex-col gap-10 sm:grid sm:grid-cols-2 md:grid-cols-3">
            {navigation.map((group) => (
              <div key={group.title} className="flex flex-col gap-3">
                <h3 className="text-sm font-semibold uppercase tracking-wide text-foreground">
                  {group.title}
                </h3>
                <ul className="flex flex-col gap-2">
                  {group.items.map((item) => (
                    <li key={item.label}>
                      <Link
                        href={item.href}
                        className="text-sm text-muted-foreground transition hover:text-foreground"
                        target={item.target ?? "_self"}
                        rel={resolveRel(item.target)}
                      >
                        {item.label}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
            {contact ? (
              <div className="flex flex-col gap-3">
                <h3 className="text-sm font-semibold uppercase tracking-wide text-foreground">
                  {contact.heading}
                </h3>
                <div className="flex flex-col gap-2 text-sm text-muted-foreground">
                  {contact.addressLines.map((line) => (
                    <span key={line}>{line}</span>
                  ))}
                  {contact.phone ? (
                    <Link
                      href={contact.phone.href}
                      className="text-sm font-semibold text-foreground transition hover:text-muted-foreground"
                      target={contact.phone.target ?? "_self"}
                      rel={resolveRel(contact.phone.target)}
                    >
                      {contact.phone.label}
                    </Link>
                  ) : null}
                  {contact.email ? (
                    <Link
                      href={contact.email.href}
                      className="text-sm font-semibold text-foreground transition hover:text-muted-foreground"
                      target={contact.email.target ?? "_self"}
                      rel={resolveRel(contact.email.target)}
                    >
                      {contact.email.label}
                    </Link>
                  ) : null}
                </div>
              </div>
            ) : null}
          </div>
        </div>
        <div className="flex flex-col gap-6 border-t border-border pt-6 sm:flex-row sm:items-center sm:justify-between">
          <div className="flex flex-wrap items-center gap-4">
            {socialLinks?.map((social) => (
              <Link
                key={social.label}
                href={social.href}
                className="flex h-10 w-10 items-center justify-center rounded-full border border-border text-muted-foreground transition hover:border-foreground hover:text-foreground"
                target={social.target ?? "_self"}
                rel={resolveRel(social.target)}
                aria-label={social.label}
              >
                <DynamicIcon name={social.iconName} className="h-5 w-5" />
              </Link>
            ))}
          </div>
          <div className="flex flex-col items-start gap-3 sm:items-end">
            {bottomLinks?.length ? (
              <div className="flex flex-wrap gap-4 text-sm text-muted-foreground">
                {bottomLinks.map((link) => (
                  <Link
                    key={link.label}
                    href={link.href}
                    className="transition hover:text-foreground"
                    target={link.target ?? "_self"}
                    rel={resolveRel(link.target)}
                  >
                    {link.label}
                  </Link>
                ))}
              </div>
            ) : null}
            <p className="text-xs text-muted-foreground">
              {copyright}
            </p>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footers03;
