import * as React from "react";
import Link from "@/components/Link";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface FaqItem {
  question: string;
  answer: string;
}

interface ActionLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface SimpleFaq01Props {
  categoryLabel?: string;
  heading?: string;
  description?: string;
  faqs?: FaqItem[];
  cta?: ActionLink | null;
  supportText?: string;
  supportLink?: ActionLink | null;
  accordionType?: "single" | "multiple";
  collapsible?: boolean;
  className?: string;
}

const defaultFaqs: FaqItem[] = [
  {
    question: "How does the onboarding process work?",
    answer: "Once you create an account, our team will guide you through a structured setup that typically takes less than a week. You'll receive checklists, video tutorials, and live support whenever you need it.",
  },
  {
    question: "Can I integrate the platform with my existing tools?",
    answer: "Yes. We offer native integrations with popular CRMs, marketing automation platforms, and analytics tools. A robust API is also available for custom workflows.",
  },
  {
    question: "What kind of support is included in the subscription?",
    answer: "All plans include 24/7 chat support, a dedicated success manager, and quarterly strategy reviews to keep your team aligned on outcomes.",
  },
  {
    question: "Do you offer discounts for annual billing?",
    answer: "Absolutely. Annual plans include a 15% savings compared to the monthly rate, along with access to exclusive training sessions for your team.",
  },
];

export function SimpleFaq01({
  categoryLabel = "Support",
  heading = "Frequently Asked Questions",
  description = "Find detailed answers to the most common questions our customers ask before getting started.",
  faqs = defaultFaqs,
  cta = {
    label: "Talk to our team",
    href: "/contact",
    target: "_self",
  },
  supportText = "Need a personalized walkthrough?",
  supportLink = {
    label: "Browse the help center",
    href: "/help-center",
    target: "_self",
  },
  accordionType = "single",
  collapsible = true,
  className,
}: SimpleFaq01Props): JSX.Element {
  return (
    <section className={cn("w-full", className)}>
      <div className="mx-auto flex w-full max-w-4xl flex-col gap-10 px-4 py-12 sm:gap-12 sm:py-16 lg:py-20">
        <div className="flex flex-col gap-4 text-center">
          {categoryLabel ? (
            <span className="text-sm font-medium uppercase tracking-wide text-primary">
              {categoryLabel}
            </span>
          ) : null}
          {heading ? (
            <h2 className="text-3xl font-semibold tracking-tight text-foreground sm:text-4xl">
              {heading}
            </h2>
          ) : null}
          {description ? (
            <p className="mx-auto max-w-2xl text-base text-muted-foreground sm:text-lg">
              {description}
            </p>
          ) : null}
        </div>

        <Accordion
          type={accordionType}
          collapsible={collapsible}
          className="w-full rounded-lg border border-border bg-card px-4 py-2 shadow-sm"
        >
          {faqs.map((faq, index) => (
            <AccordionItem value={`faq-${index}`} key={`faq-${index}`}>
              <AccordionTrigger className="text-left text-base font-medium text-foreground">
                {faq.question}
              </AccordionTrigger>
              <AccordionContent className="text-sm leading-relaxed text-muted-foreground">
                {faq.answer}
              </AccordionContent>
            </AccordionItem>
          ))}
        </Accordion>

        {cta || supportText || supportLink ? (
          <div className="flex flex-col gap-4 rounded-lg border border-dashed border-border p-6 sm:flex-row sm:items-center sm:justify-between">
            <div className="space-y-1 text-center sm:text-left">
              {supportText ? (
                <p className="text-base font-medium text-foreground">
                  {supportText}
                </p>
              ) : null}
              {supportLink ? (
                <Link
                  href={supportLink.href}
                  target={supportLink.target}
                  rel={supportLink.rel}
                  className="text-sm font-semibold text-primary underline-offset-4 hover:underline"
                >
                  {supportLink.label}
                </Link>
              ) : null}
            </div>
            {cta ? (
              <Button asChild className="h-11 w-full text-sm font-semibold sm:w-auto">
                <Link
                  href={cta.href}
                  target={cta.target}
                  rel={cta.rel}
                  className="flex items-center justify-center px-6"
                >
                  {cta.label}
                </Link>
              </Button>
            ) : null}
          </div>
        ) : null}
      </div>
    </section>
  );
}

export default SimpleFaq01;
