import * as React from "react";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

type AvatarProps = {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  type?: "profile" | "logo" | "thumbnail" | "icon" | "other";
};

type CompanyProps = {
  name?: string;
  logo?: AvatarProps;
};

export type TestimonialItem = {
  quote: string;
  authorName: string;
  authorRole?: string;
  avatar: AvatarProps;
  company?: CompanyProps;
};

type CallToAction = {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
  className?: string;
};

export interface Testimonials01Props {
  title?: string;
  description?: string;
  testimonials?: TestimonialItem[];
  cta?: CallToAction;
  className?: string;
}

const defaultTestimonials: TestimonialItem[] = [
  {
    quote:
      "“Working with this platform has transformed the way our team collaborates. The intuitive interface and responsive support make all the difference.”",
    authorName: "Amelia Chen",
    authorRole: "Head of Operations, Brightwave",
    avatar: {
      src: "/images/testimonials/amelia-chen.jpg",
      alt: "Portrait of Amelia Chen",
      width: 56,
      height: 56,
      type: "profile"
    },
    company: {
      name: "Brightwave"
    }
  },
  {
    quote:
      "“Our productivity has increased by over 40%. The analytics and reporting give us clear insights that we rely on every day.”",
    authorName: "Marco Rivera",
    authorRole: "Product Manager, Flowspace",
    avatar: {
      src: "/images/testimonials/marco-rivera.jpg",
      alt: "Portrait of Marco Rivera",
      width: 56,
      height: 56,
      type: "profile"
    },
    company: {
      name: "Flowspace"
    }
  },
  {
    quote:
      "“A reliable partner for scaling teams. The onboarding process was seamless, and our customers love the new experience.”",
    authorName: "Priya Desai",
    authorRole: "Customer Success Lead, Northwind",
    avatar: {
      src: "/images/testimonials/priya-desai.jpg",
      alt: "Portrait of Priya Desai",
      width: 56,
      height: 56,
      type: "profile"
    },
    company: {
      name: "Northwind"
    }
  }
];

const defaultCta: CallToAction = {
  label: "Read more stories",
  href: "/case-studies",
  target: "_self"
};

const sectionId = "testimonials";

const Testimonials01: React.FC<Testimonials01Props> = ({
  title = "Loved by teams worldwide",
  description = "Discover how fast-growing teams build stronger relationships with their customers using our platform.",
  testimonials = defaultTestimonials,
  cta = defaultCta,
  className
}) => {
  if (!testimonials.length) {
    return null;
  }

  return (
    <section id={sectionId} className={cn("w-full", className)}>
      <div className="mx-auto flex max-w-6xl flex-col gap-12 px-4 py-16 sm:px-6 lg:px-8">
        <header className="flex flex-col items-center gap-6 text-center">
          <div className="flex flex-col gap-3">
            <h2 className="text-3xl font-semibold tracking-tight text-gray-900 sm:text-4xl">
              {title}
            </h2>
            {description ? (
              <p className="mx-auto max-w-2xl text-base text-gray-600 sm:text-lg">
                {description}
              </p>
            ) : null}
          </div>
          {cta ? (
            <Button asChild>
              <Link
                href={cta.href}
                target={cta.target ?? "_self"}
                rel={cta.rel ?? (cta.target === "_blank" ? "noopener noreferrer" : undefined)}
                className={cn("inline-flex items-center justify-center", cta.className)}
              >
                {cta.label}
              </Link>
            </Button>
          ) : null}
        </header>
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {testimonials.map((testimonial, index) => (
            <article
              key={`${testimonial.authorName}-${index}`}
              className="flex h-full flex-col justify-between gap-6 rounded-2xl border border-gray-200 bg-white/80 p-6 shadow-sm backdrop-blur"
            >
              <p className="text-base text-gray-700">{testimonial.quote}</p>
              <div className="flex items-center gap-4">
                <div className="relative h-14 w-14 overflow-hidden rounded-full border border-gray-200">
                  <Image
                    src={testimonial.avatar.src}
                    alt={testimonial.avatar.alt}
                    width={testimonial.avatar.width ?? 56}
                    height={testimonial.avatar.height ?? 56}
                    className={cn("h-full w-full object-cover", testimonial.avatar.className)}
                    type={testimonial.avatar.type ?? "profile"}
                  />
                </div>
                <div className="flex flex-col">
                  <span className="font-semibold text-gray-900">{testimonial.authorName}</span>
                  {testimonial.authorRole ? (
                    <span className="text-sm text-gray-600">{testimonial.authorRole}</span>
                  ) : null}
                  {testimonial.company?.name ? (
                    <span className="text-sm text-gray-500">{testimonial.company.name}</span>
                  ) : null}
                  {testimonial.company?.logo ? (
                    <div className="mt-2 h-6 w-auto">
                      <Image
                        src={testimonial.company.logo.src}
                        alt={testimonial.company.logo.alt}
                        width={testimonial.company.logo.width ?? 96}
                        height={testimonial.company.logo.height ?? 24}
                        className={cn("h-6 w-auto object-contain", testimonial.company.logo.className)}
                        type={testimonial.company.logo.type ?? "logo"}
                      />
                    </div>
                  ) : null}
                </div>
              </div>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials01;
