import type { JSX } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image, { type ImageProps } from "@/components/Image";
import Link, { type LinkProps } from "@/components/Link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

type ButtonVariant = "default" | "secondary" | "destructive" | "outline" | "ghost" | "link";
type ButtonSize = "default" | "sm" | "lg" | "icon";

interface EnhancedLinkProps extends LinkProps {
  label: string;
  className?: string;
  icon?: Icon;
}

interface ActionLink extends EnhancedLinkProps {
  variant?: ButtonVariant;
  size?: ButtonSize;
}

interface Testimonial {
  id: string;
  quote: string;
  authorName: string;
  authorRole?: string;
  companyName?: string;
  avatar: ImageProps;
  rating?: number;
  icon?: Icon;
  link?: EnhancedLinkProps;
}

export interface Testimonials02Props {
  id?: string;
  eyebrow?: string;
  title: string;
  description?: string;
  testimonials: Testimonial[];
  primaryAction?: ActionLink;
  secondaryAction?: ActionLink;
  align?: "left" | "center";
}

const DEFAULT_BUTTON_SIZE: ButtonSize = "lg";

const resolveTarget = (link?: LinkProps): "_blank" | "_self" => link?.target ?? "_self";

const resolveRel = (link?: LinkProps): string | undefined => {
  const target = resolveTarget(link);
  return link?.rel ?? (target === "_blank" ? "noopener noreferrer" : undefined);
};

export default function Testimonials02({
  id,
  eyebrow,
  title,
  description,
  testimonials,
  primaryAction,
  secondaryAction,
  align = "center",
}: Testimonials02Props): JSX.Element | null {
  if (!Array.isArray(testimonials) || testimonials.length === 0) {
    return null;
  }

  const alignmentClasses = align === "left" ? "items-start text-left" : "items-center text-center";
  const actionsJustifyClasses = align === "left" ? "justify-start" : "justify-center";

  return (
    <section id={id} aria-labelledby={id ? `${id}-title` : undefined} className="w-full">
      <div className="mx-auto flex w-full max-w-6xl flex-col gap-12 px-4 py-16 sm:px-6 lg:px-8">
        <div className={cn("flex flex-col gap-6", alignmentClasses)}>
          {eyebrow ? (
            <span className="text-sm font-semibold uppercase tracking-widest text-primary">
              {eyebrow}
            </span>
          ) : null}
          <h2 id={id ? `${id}-title` : undefined} className="text-3xl font-bold tracking-tight text-foreground sm:text-4xl">
            {title}
          </h2>
          {description ? (
            <p className="max-w-3xl text-base text-muted-foreground">
              {description}
            </p>
          ) : null}
          {primaryAction || secondaryAction ? (
            <div className={cn("flex flex-wrap items-center gap-4", actionsJustifyClasses)}>
              {primaryAction ? (
                <Button
                  asChild
                  size={primaryAction.size ?? DEFAULT_BUTTON_SIZE}
                  variant={primaryAction.variant ?? "default"}
                >
                  <Link
                    href={primaryAction.href}
                    target={resolveTarget(primaryAction)}
                    rel={resolveRel(primaryAction)}
                    className={cn("inline-flex items-center gap-2 font-semibold", primaryAction.className)}
                  >
                    {primaryAction.icon ? (
                      <DynamicIcon name={primaryAction.icon.name} className="h-4 w-4" />
                    ) : null}
                    <span>{primaryAction.label}</span>
                  </Link>
                </Button>
              ) : null}
              {secondaryAction ? (
                <Button
                  asChild
                  size={secondaryAction.size ?? DEFAULT_BUTTON_SIZE}
                  variant={secondaryAction.variant ?? "outline"}
                >
                  <Link
                    href={secondaryAction.href}
                    target={resolveTarget(secondaryAction)}
                    rel={resolveRel(secondaryAction)}
                    className={cn("inline-flex items-center gap-2 font-semibold", secondaryAction.className)}
                  >
                    {secondaryAction.icon ? (
                      <DynamicIcon name={secondaryAction.icon.name} className="h-4 w-4" />
                    ) : null}
                    <span>{secondaryAction.label}</span>
                  </Link>
                </Button>
              ) : null}
            </div>
          ) : null}
        </div>
        <div className="grid gap-8 md:grid-cols-2 xl:grid-cols-3">
          {testimonials.map((testimonial) => {
            const normalizedRating = Math.max(0, Math.min(5, Math.round(testimonial.rating ?? 0)));

            return (
              <article
                key={testimonial.id}
                className="flex h-full flex-col justify-between gap-6 rounded-3xl border border-border bg-background p-8 shadow-sm transition-shadow hover:shadow-md"
              >
                <div className="flex flex-col gap-6">
                  {testimonial.icon ? (
                    <DynamicIcon name={testimonial.icon.name} className="h-8 w-8 text-primary" />
                  ) : null}
                  <p className="text-lg font-medium leading-relaxed text-foreground">
                    &ldquo;{testimonial.quote}&rdquo;
                  </p>
                </div>
                <div className="flex flex-col gap-4">
                  <div className="flex items-center justify-between gap-4">
                    <div className="flex items-center gap-4">
                      <Image
                        {...testimonial.avatar}
                        className={cn("h-14 w-14 rounded-full object-cover", testimonial.avatar.className)}
                      />
                      <div className="flex flex-col">
                        <span className="text-base font-semibold text-foreground">
                          {testimonial.authorName}
                        </span>
                        {testimonial.authorRole ? (
                          <span className="text-sm text-muted-foreground">
                            {testimonial.authorRole}
                          </span>
                        ) : null}
                        {testimonial.companyName ? (
                          <span className="text-sm text-muted-foreground">
                            {testimonial.companyName}
                          </span>
                        ) : null}
                      </div>
                    </div>
                    {testimonial.rating !== undefined ? (
                      <div className="flex items-center gap-1">
                        {Array.from({ length: 5 }).map((_, index) => (
                          <DynamicIcon
                            key={index}
                            name="Star"
                            className={cn(
                              "h-4 w-4",
                              index < normalizedRating ? "text-primary" : "text-muted-foreground opacity-40"
                            )}
                          />
                        ))}
                      </div>
                    ) : null}
                  </div>
                  {testimonial.link ? (
                    <Link
                      href={testimonial.link.href}
                      target={resolveTarget(testimonial.link)}
                      rel={resolveRel(testimonial.link)}
                      className={cn(
                        "inline-flex items-center gap-2 text-sm font-semibold text-primary hover:text-primary/80",
                        testimonial.link.className
                      )}
                    >
                      {testimonial.link.icon ? (
                        <DynamicIcon name={testimonial.link.icon.name} className="h-4 w-4" />
                      ) : null}
                      <span>{testimonial.link.label}</span>
                    </Link>
                  ) : null}
                </div>
              </article>
            );
          })}
        </div>
      </div>
    </section>
  );
}
